cream_color = "lemonchiffon";
pastry_color = "wheat";
chocolate_color = "saddlebrown";

// Main profiterole module
module profiterole() {
    // Base choux pastry shell
    difference() {
        // Outer pastry sphere
        color(pastry_color) 
        sphere(d = prof_size, $fn = 64);

        // Hollow out the inside
        translate([0, 0, -wall_thickness/2])
            sphere(d = prof_size - 2*wall_thickness, $fn = 64);

        // Flatten bottom slightly for printing
        translate([0, 0, -prof_size/2 - 1])
            cube([prof_size*2, prof_size*2, prof_size], center = true);
    }

    // Add pastry texture - small bumps on surface
    for(i = [0:15]) {
        rotate([0, 0, i*24 + 12])
            translate([0, prof_size/2 - 2, 0])
                rotate([90, 0, 0])
                    scale([1, 1, 0.6])
                        sphere(d = 3 + sin(i*3)*1.5, $fn = 16);
    }

    // Cream filling visible from top
    translate([0, 0, prof_size/4])
        color(cream_color)
        difference() {
            sphere(d = prof_size - 6, $fn = 48);
            translate([0, 0, -prof_size/4])
                cube([prof_size, prof_size, prof_size/2], center = true);
        }

    // Chocolate ganache topping
    color(chocolate_color)
    difference() {
        // Main ganache dome
        translate([0, 0, prof_size/2 - 2])
            scale([1, 1, 0.3])
                sphere(d = prof_size - 4, $fn = 64);

        // Cut to fit top curve
        translate([0, 0, prof_size/2 - 8])
            cube([prof_size, prof_size, 10], center = true);

        // Drizzle effect
        for(j = [0:5]) {
            rotate([0, 0, j*60])
                translate([prof_size/4, 0, prof_size/2 - 1])
                    rotate([90, 0, 30])
                        cylinder(h = 12, r1 = 2, r2 = 0.5, $fn = 8);
        }
    }

    // Powdered sugar dusting effect
    for(k = [0:20]) {
        rotate([0, 0, k*18])
            translate([prof_size/2 * cos(k*3), prof_size/2 * sin(k*3), prof_size/2 - 0.5])
                color("white", 0.7)
                sphere(d = 0.5 + sin(k*5)*0.3, $fn = 8);
    }
}

// Create the profiterole
