sugar_color = "#F5F5DC";   // Creamy white
cinnamon_color = "#8B4513";  // Cinnamon brown

// Create a single churro with ridges
module churro_body() {
    color(churro_color) {
        // Main cylinder body
        difference() {
            cylinder(h = churro_length, d = churro_diameter, center = true);

            // Hollow center for printing
            translate([0, 0, -churro_length/2 - 1])
                cylinder(h = churro_length + 2, d = churro_diameter - 4, center = false);
        }

        // Create ridges along the length
        for (i = [0:ridge_count-1]) {
            rotate([0, 0, i * (360/ridge_count)]) {
                translate([churro_diameter/2 - ridge_height/2, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h = churro_length, d = ridge_height, center = true);
            }
        }

        // Tapered ends
        translate([0, 0, churro_length/2])
            cylinder(h = 5, d1 = churro_diameter, d2 = churro_diameter/2, center = false);

        translate([0, 0, -churro_length/2])
            cylinder(h = 5, d1 = churro_diameter, d2 = churro_diameter/2, center = false);
    }
}

// Create sugar coating texture
module sugar_coating() {
    color(sugar_color) {
        // Random sugar grains on surface
        for (i = [0:200]) {
            angle = rands(0, 360, 1)[0];
            height = rands(-churro_length/2 + 5, churro_length/2 - 5, 1)[0];
            radius = churro_diameter/2 + 0.2;

            rotate([0, 0, angle])
                translate([radius * cos(rands(0, 360, 1)[0]), 
                          radius * sin(rands(0, 360, 1)[0]), 
                          height])
                    sphere(d = sugar_grain_size);
        }
    }

    // Cinnamon streaks
    color(cinnamon_color) {
        for (i = [0:50]) {
            angle = rands(0, 360, 1)[0];
            height = rands(-churro_length/2 + 5, churro_length/2 - 5, 1)[0];
            radius = churro_diameter/2 + 0.1;

            rotate([0, 0, angle])
                translate([radius * cos(rands(0, 360, 1)[0]), 
                          radius * sin(rands(0, 360, 1)[0]), 
                          height])
                    sphere(d = sugar_grain_size * 1.5);
        }
    }
}

// Create slight bend for realistic shape
module bent_churro() {
    // Create a gentle curve
    for (i = [0:churro_length/2]) {
        z_pos = -churro_length/2 + i * 2;
        x_offset = sin(z_pos * 3.14159 / churro_length) * 3;

        translate([x_offset, 0, z_pos])
            rotate([0, 0, x_offset])
                intersection() {
                    translate([0, 0, -1])
                        cylinder(h = 2.1, d = churro_diameter + 0.5, center = true);
                    churro_body();
                }
    }
}

// Assemble the complete churro
translate([0, 0, 0])
    union() {
        // Main churro body
        rotate([90, 0, 0])
            bent_churro();

        // Sugar coating
        rotate([90, 0, 0])
            sugar_coating();
    }

