module galette() {
    // Base crust - slightly irregular disk
    difference() {
        scale([1, 1.1, 1]) 
            cylinder(h=3, d=80, $fn=32);

        // Random texture on bottom
        for(i=[0:20]) {
            translate([rand(i*3)-10, rand(i*5)-10, -0.5])
                sphere(d=rand(i*7)+3);
        }
    }

    // Raised crust edge
    difference() {
        // Main raised edge
        translate([0, 0, 3])
            cylinder(h=5, d1=78, d2=74, $fn=32);

        // Remove center to create hollow
        translate([0, 0, -1])
            cylinder(h=7, d=65, $fn=32);

        // Create folded edge texture
        for(a=[0:30:360]) {
            rotate([0, 0, a+rand(a*2)*5])
            translate([rand(a)*15+25, 0, 3])
                rotate([90, 0, 0])
                    cylinder(h=rand(a)+8, d1=6, d2=4, $fn=8);
        }
    }

    // Fruit filling layer
    color(color_filling)
    translate([0, 0, 3])
        cylinder(h=4, d=64, $fn=32);

    // Apple slices arranged in spiral
    for(i=[0:8]) {
        angle = i * 45;
        radius = 10 + i * 3;
        color(color_apple)
        translate([cos(angle)*radius, sin(angle)*radius, 7.5])
            rotate([0, 0, angle + 90])
            scale([1, 0.3, 0.2])
                sphere(d=12);
    }

    // Pear slices between apple
    for(i=[0:7]) {
        angle = i * 45 + 22.5;
        radius = 15 + i * 2.5;
        color(color_pear)
        translate([cos(angle)*radius, sin(angle)*radius, 7.5])
            rotate([0, 0, angle + 90])
            scale([1.2, 0.4, 0.15])
                sphere(d=10);
    }

    // Central decorative pattern
    for(a=[0:45:360]) {
        color(color_apple)
        rotate([0, 0, a])
        translate([10, 0, 7.8])
            cylinder(h=0.5, d=4, $fn=6);
    }

    // Sugar crystals on top
    for(i=[0:50]) {
        color("#FFFFFF")
        translate([rand(i*123)*70-35, rand(i*456)*70-35, 8 + rand(i*789)*0.5])
            sphere(d=0.5 + rand(i));
    }

    // Almond slices
    for(i=[0:12]) {
        angle = i * 30;
        color("#DEB887")
        translate([cos(angle)*20, sin(angle)*20, 8])
            rotate([90, 0, angle])
            scale([1, 0.2, 0.1])
                cube([8, 3, 1], center=true);
    }
}

