$fs = 0.5;

// Main parameters
turnover_length = 70;
turnover_width = 50;
turnover_height = 18;
crust_thickness = 2.5;
seam_thickness = 1.5;

// Create the turnover
module turnover() {
    // Main pastry body - half-circle extrusion
    difference() {
        // Outer shell
        linear_extrude(height = turnover_height, convexity = 10)
            offset(r = crust_thickness)
                offset(r = -crust_thickness)
                    circle(d = turnover_length);

        // Hollow interior
        translate([0, 0, crust_thickness])
            linear_extrude(height = turnover_height - crust_thickness*2, convexity = 10)
                circle(d = turnover_length - crust_thickness*2);
    }

    // Flatten to ellipse shape
    scale([1, turnover_width/turnover_length, 1])
        children();
}

// Create crimped edge pattern
module crimped_edge() {
    crimps = 18;
    for(i = [0:crimps-1]) {
        rotate([0, 0, i * 360/crimps])
            translate([turnover_length/2 - seam_thickness/2, 0, turnover_height/2])
                rotate([90, 0, 0])
                    cylinder(h = seam_thickness*1.5, r1 = seam_thickness, r2 = 0.5, $fn = 6);
    }
}

// Create surface texture (small bumps for pastry)
module pastry_texture() {
    texture_count = 30;
    for(i = [0:texture_count-1]) {
        for(j = [0:texture_count/2-1]) {
            x = (i - texture_count/2) * 2.5;
            y = (j - texture_count/4) * 2.5;
            if(norm([x, y]) < turnover_length/2 - crust_thickness - 5) {
                translate([x, y, turnover_height - 0.5])
                    scale([1, turnover_width/turnover_length, 1])
                        sphere(r = 0.3 + 0.2 * sin(i*2+j*3), $fn = 8);
            }
        }
    }
}

// Create vent holes
module vent_holes() {
    holes = 3;
    for(i = [0:holes-1]) {
        angle = 45 + i * 45;
        rotate([0, 0, angle])
            translate([turnover_length/4, 0, turnover_height/2])
                rotate([0, 90, 0])
                    cylinder(h = turnover_height, r = 1.5, $fn = 12);
    }
}

// Create filling that shows at edges
module visible_filling() {
    // Small gaps at seam showing filling
    gap_count = 8;
    for(i = [0:gap_count-1]) {
        angle = i * 360/gap_count;
        rotate([0, 0, angle])
            translate([turnover_length/2 - 1, 0, turnover_height/2])
                scale([1, 0.8, 1])
                    sphere(r = 2.5);
    }
}

// Assembly
color("#D4A574")  // Golden brown pastry color
union() {
    turnover();

    // Only add texture to top surface
    translate([0, 0, 0])
        pastry_texture();

    // Sealed edge
    color("#B8956A")
        crimped_edge();

    // Vent holes
    color("#8B4513")  // Darker cooked color
        vent_holes();

    // Filling showing through
    color("#7B3F00")  // Brown filling color
        visible_filling();
}

