// Croquembouche - Cone of cream puffs with caramel threads
// Realistic style, medium complexity

// Colors
$custard = [1, 0.9, 0.7];
$caramel = [0.8, 0.5, 0.2];
$chocolate = [0.3, 0.15, 0];

// Main module
module croquembouche() {
    // Base cream puffs in pyramid
    cream_puff_pyramid();

    // Caramel threads connecting puffs
    caramel_threads();

    // Decorative spun sugar top
    spun_sugar_crown();
}

// Single cream puff with custard filling
module cream_puff() {
    color($custard) {
        // Bottom half
        difference() {
            sphere(d=12, $fn=32);
            translate([0, 0, 3])
                sphere(d=11.5, $fn=32);
        }

        // Custard center
        translate([0, 0, 1])
            color([1, 0.95, 0.8])
            sphere(d=8, $fn=32);
    }

    // Top dome
    color($caramel) {
        translate([0, 0, 2])
            sphere(d=12, $fn=32);
    }

    // Powdered sugar dusting
    for(i=[0:15]) {
        rotate([0, 0, i*24])
        translate([rand(3,5), 0, rand(4,5.5)])
            sphere(d=0.3, $fn=8);
    }
}

// Create pyramid of puffs
module cream_puff_pyramid() {
    // Base layer (7 puffs)
    for(i=[-3:3]) {
        translate([i*13, 0, 0])
            cream_puff();
    }

    // Second layer (6 puffs)
    for(i=[-2.5:2.5]) {
        translate([i*13, 11.25, 11])
            cream_puff();
    }

    // Third layer (5 puffs)
    for(i=[-2:2]) {
        translate([i*13, 22.5, 22])
            cream_puff();
    }

    // Fourth layer (4 puffs)
    for(i=[-1.5:1.5]) {
        translate([i*13, 33.75, 33])
            cream_puff();
    }

    // Fifth layer (3 puffs)
    for(i=[-1:1]) {
        translate([i*13, 45, 44])
            cream_puff();
    }

    // Sixth layer (2 puffs)
    for(i=[-0.5:0.5]) {
        translate([i*13, 56.25, 55])
            cream_puff();
    }

    // Top puff
    translate([0, 67.5, 66])
        cream_puff();
}

// Caramel threads between puffs
module caramel_threads() {
    color($caramel) {
        // Vertical threads
        for(layer=[0:5]) {
            y = layer * 11.25;
            z = layer * 11;
            count = 7 - layer;
            for(i=[-(count-1)/2:(count-1)/2]) {
                x = i * 13;
                translate([x, y, z])
                    cylinder(h=11, d1=1.5, d2=0.5, $fn=8);
            }
        }

        // Horizontal threads between adjacent puffs
        for(layer=[0:5]) {
            y = layer * 11.25;
            z = layer * 11 + 5.5;
            count = 7 - layer;
            for(i=[-(count-1)/2+0.5:(count-1)/2-0.5]) {
                x = i * 13;
                translate([x, y, z])
                    rotate([90, 0, 90])
                    cylinder(h=13, d=1, $fn=8);
            }
        }

        // Diagonal threads
        for(layer=[0:4]) {
            y1 = layer * 11.25;
            z1 = layer * 11 + 5.5;
            y2 = (layer+1) * 11.25;
            z2 = (layer+1) * 11 + 5.5;
            count = 7 - layer;

            for(i=[-(count-1)/2:(count-1)/2-1]) {
                x = i * 13;
                dx = 6.5;
                dy = 5.625;
                dz = 5.5;
                dist = sqrt(dx*dx + dy*dy + dz*dz);

                translate([x+6.5, y1+dy, z1+dz])
                    rotate([atan2(dz, dy), 0, atan2(dx, sqrt(dy*dy+dz*dz))])
                    cylinder(h=dist, d=0.8, $fn=6);
            }
        }
    }
}

// Spun sugar crown decoration
module spun_sugar_crown() {
    color($caramel) {
        // Base ring
        translate([0, 67.5, 72])
            rotate([90, 0, 0])
            cylinder(h=3, r1=15, r2=12, $fn=32);

        // Spun sugar loops
        for(i=[0:8]) {
            rotate([0, 0, i*40])
            translate([10, 67.5, 72])
                rotate([0, 90, 0])
                cylinder(h=20, d1=2, d2=0.5, $fn=8);

            rotate([0, 0, i*40+20])
            translate([8, 67.5, 80])
                rotate([0, 90, 0])
                cylinder(h=15, d1=1.5, d2=0.5, $fn=8);
        }

        // Top spiral
        for(i=[0:20]) {
            t = i/20;
            angle = t * 360 * 2;
            radius = 8 - t * 6;
            height = 72 + t * 10;

            translate([cos(angle)*radius, 67.5 + sin(angle)*radius, height])
                sphere(d=1, $fn=6);
        }
    }
}

