// Main babka module
module babka() {
    // Base loaf shape
    difference() {
        union() {
            // Main loaf body
            color(dough) 
            rounded_cube([60, 35, 35], 5);

            // Chocolate layers visible on sides
            color(chocolate)
            for(i = [-1, 1]) {
                translate([i * 20, 0, -5])
                cube([5, 38, 40]);
            }
