// Helper module for rounded cube
module rounded_cube(size, radius) {
    hull() {
        for(dx = [0, 1], dy = [0, 1], dz = [0, 1]) {
            translate([
                dx * (size[0] - 2 * radius),
                dy * (size[1] - 2 * radius),
                dz * (size[2] - 2 * radius)
            ])
            sphere(r = radius);
        }
    }
}

// Helper for random values
function rand(min, max) = min + (max - min) * rands(0, 1, 1)[0];

