// Module for creating the cone shape
module krumkake_cone() {
    // Create main cone body using hull of cylinders
    hull() {
        cylinder(h=wall_thickness, r1=krumkake_radius, r2=krumkake_radius * 0.9);
        translate([0, 0, krumkake_height - wall_thickness])
            cylinder(h=wall_thickness, r1=krumkake_radius * 0.9, r2=krumkake_radius * 0.7);
    }
}

// Module for creating waffle pattern
module waffle_pattern() {
    // Horizontal and vertical lines creating grid pattern
    for(i = [-krumkake_radius + 5 : 5 : krumkake_radius - 5]) {
        // Vertical lines
        translate([i, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=krumkake_height * 0.9, r=0.5, center=true);

        // Horizontal lines
        for(j = [0 : 5 : krumkake_height - 5]) {
            translate([0, i, j])
                rotate([90, 0, 0])
                    cylinder(h=krumkake_radius * 2, r=0.5, center=true);
        }
    }

    // Diamond pattern details
    for(i = [-krumkake_radius + 7.5 : 5 : krumkake_radius - 7.5]) {
        for(j = [2.5 : 5 : krumkake_height - 2.5]) {
            translate([i, 0, j])
                scale([1, 0.5, 1])
                    sphere(r=1.2);
        }
    }
}

// Module for rolled edge detail
module rolled_edge() {
    // Create overlapping layers at the seam
    for(i = [0 : 2 : krumkake_height - 5]) {
        translate([krumkake_radius * 0.8, 0, i])
            rotate([0, 90, 0])
                cylinder(h=3, r=wall_thickness * 1.5);
    }
}

