    difference() {
        // Outer shell
        krumkake_cone();

        // Hollow interior
        translate([0, 0, wall_thickness])
            cylinder(h=krumkake_height - wall_thickness * 2, 
                     r1=krumkake_radius - wall_thickness, 
                     r2=krumkake_radius * 0.7 - wall_thickness);
