                translate([rand(-radius*0.8, radius*0.8), 
                          rand(-radius*0.8, radius*0.8), 
                          rand(-radius*0.8, radius*0.8)]) {
                    color(rasgulla_color) {
                        sphere(r = rand(1, 2.5));
                    }
                }
            }
        }
    }
}

// Module to create syrup pool
module syrup_pool() {
    // Base dish
    color(dish_color) {
        difference() {
            cylinder(h = base_height, r1 = 55, r2 = 50);
            cylinder(h = base_height + 1, r1 = 45, r2 = 45);
        }
    }

    // Syrup liquid
    color(syrup_color) {
        difference() {
            cylinder(h = syrup_thickness, r1 = 45, r2 = 40);
            translate([0, 0, -0.1]) {
                cylinder(h = syrup_thickness + 0.2, r1 = 35, r2 = 35);
