    // Create syrup pool
    syrup_pool();

    // Place rasgulla balls in syrup
    // Main ball slightly floating
    rasgulla_ball(rasgulla_radius, [0, 0, base_height + syrup_thickness + rasgulla_radius - 3]);

    // Additional smaller balls for realism
    rasgulla_ball(rasgulla_radius * 0.7, [-25, 15, base_height + syrup_thickness + rasgulla_radius * 0.7 - 2]);
    rasgulla_ball(rasgulla_radius * 0.8, [20, -10, base_height + syrup_thickness + rasgulla_radius * 0.8 - 2.5]);

    // Syrup drips on balls
    for (i = [0:5]) {
        rotate([0, 0, i * 60]) {
            translate([rand(5, 15), rand(-5, 5), base_height + syrup_thickness]) {
                color(syrup_color) {
                    cylinder(h = rand(2, 4), r1 = rand(1, 2), r2 = 0.5);
                }
