// Colors
syrup_color = [139/255, 69/255, 19/255, 0.9];  // Deep amber
dumpling_color = [210/255, 180/255, 140/255];  // Golden brown
highlight_color = [1, 1, 0.9, 0.4];  // Syrup highlights

module gulab_jamun() {
    // Main dumpling body
    color(dumpling_color) {
        scale([1, 1, base_flatten]) 
            sphere(d = gulab_diameter);
    }

    // Slightly flattened top for realistic shape
    translate([0, 0, gulab_height * base_flatten - 2])
        scale([1, 1, 0.3])
            sphere(d = gulab_diameter * 0.9);

    // Surface texture - small bumps for fried appearance
    for (i = [0:15]) {
        rotate([0, 0, i * 24]) {
            translate([
                gulab_diameter/2 * 0.85 * cos(i * 37),
                gulab_diameter/2 * 0.85 * sin(i * 37),
                (i % 2) * 2 + base_flatten * gulab_height/2
            ]) {
                scale([1, 1, 0.7])
                    sphere(d = 2.5);
            }
        }
    }

    // Syrup coating layer
    color(syrup_color) {
        scale([1.08, 1.08, base_flatten * 1.05]) 
            sphere(d = gulab_diameter + syrup_thickness);
    }

