                translate([
                    gulab_diameter/2 * 0.9 * cos(j * 73),
                    gulab_diameter/2 * 0.9 * sin(j * 73),
                    base_flatten * gulab_height + 1
                ]) {
                    scale([1, 1, 0.1])
                        sphere(d = 4 + (j % 3) * 2);
                }
            }
        }
