    // Small crack details from frying
    for (k = [0:5]) {
        rotate([0, 0, k * 60]) {
            translate([
                gulab_diameter/2 * 0.7 * cos(k * 45),
                gulab_diameter/2 * 0.7 * sin(k * 45),
