// Main jalebi module
module jalebi() {
    color([0.9, 0.6, 0.1])  // Golden yellow-orange color

    // Create the spiral base
    union() {
        // Outer spiral ring
        spiral_ring();

        // Inner spiral details
        rotate([0, 0, 30])
            scale(0.7)
                spiral_ring();

        // Syrup coating on top
        syrup_layer();

        // Texture details
        sugar_crystals();
    }
}

// Create the main spiral ring
module spiral_ring() {
    // Build spiral from multiple torus segments
    for (i = [0:15:360]) {
        // Calculate spiral position
        radius = jalebi_diameter/2 - (i/360) * 15;
        if (radius > 5) {
            translate([cos(i) * radius, sin(i) * radius, 0])
                rotate([0, 0, i])
                    torus_segment(radius/10 + 2);
        }
    }
}

// Individual torus segment for spiral
module torus_segment(radius) {
    difference() {
        // Main dough body
        cylinder(h = jalebi_height, r = radius, center = true);

        // Hollow center for printing
        translate([0, 0, -jalebi_height/2 - 0.1])
            cylinder(h = jalebi_height + 0.2, r = radius - wall_thickness, center = false);
    }
}

// Syrup coating layer
module syrup_layer() {
    color([0.85, 0.5, 0.05])  // Darker syrup color

    // Create syrup coating on top surfaces
    for (i = [0:15:360]) {
        radius = jalebi_diameter/2 - (i/360) * 15;
        if (radius > 5) {
            translate([cos(i) * radius, sin(i) * radius, jalebi_height/2])
                rotate([90, 0, 0])
                    cylinder(h = radius/10 + 2, r = syrup_thickness, center = true);
        }
    }
}

// Sugar crystal details
module sugar_crystals() {
    color([1, 1, 0.8])  // White sugar color

    // Random sugar crystals on surface
    for (i = [0:30:360]) {
        angle = i + rands(0, 15, 1)[0];
        radius = rands(5, jalebi_diameter/2 - 5, 1)[0];
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        z = rands(jalebi_height/2 - 1, jalebi_height/2 + 1, 1)[0];

        translate([x, y, z])
            scale([1, 1, 0.5])
                sphere(r = 0.3 + rands(0, 0.2, 1)[0]);
    }
}

