// Turkish Delight (Lokum) - Realistic Style
// Traditional cubed confection with powdered sugar coating

// Parameters
$fn = 32;  // Smooth curves
cube_size = 12;  // Main cube dimension
powder_thickness = 1;  // Powder sugar layer
rows = 4;  // Number of cubes in row
cols = 4;  // Number of cubes in column
spacing = 2;  // Gap between cubes
total_width = rows * (cube_size + spacing) - spacing;

// Colors
color_rose = [0.9, 0.3, 0.4];  // Rose flavor
color_pistachio = [0.7, 0.8, 0.5];  // Pistachio
color_lemon = [0.95, 0.85, 0.3];  // Lemon
color_powder = [0.98, 0.97, 0.95];  // Powdered sugar

// Module for single turkish delight cube
module turkish_delight_cube(x_pos, y_pos, color_choice) {
    translate([x_pos, y_pos, 0]) {
        // Main jelly cube
        color(color_choice) {
            difference() {
                // Main cube body
                cube([cube_size, cube_size, cube_size], center = true);

                // Subtle rounding on top edges
                translate([0, 0, cube_size/2 - 1]) {
                    scale([1, 1, 0.3]) {
                        sphere(d = cube_size - 1, $fn = 20);
                    }
                }
            }
        }

        // Powdered sugar coating
        color(color_powder) {
            intersection() {
                // Powder layer
                translate([0, 0, 0]) {
                    sphere(d = cube_size + powder_thickness * 2, $fn = 8);
                }

                // Ensure powder only on surface
                cube([cube_size + 0.5, cube_size + 0.5, cube_size + 0.5], center = true);
            }
        }

        // Pistachio pieces embedded (for pistachio flavor)
        if (color_choice == color_pistachio) {
            for (i = [0:2]) {
                rotate([0, 0, i * 120]) {
                    translate([2, 0, cube_size/4]) {
                        color([0.4, 0.6, 0.3]) {
                            scale([1, 0.5, 0.3]) {
                                sphere(d = 2.5);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Create the entire tray of turkish delight
module turkish_delight_tray() {
    // Create grid of cubes
    for (x = [0:rows-1]) {
        for (y = [0:cols-1]) {
            x_pos = x * (cube_size + spacing) - total_width/2 + cube_size/2;
            y_pos = y * (cube_size + spacing) - total_width/2 + cube_size/2;

            // Alternate colors in pattern
            color_index = (x + y) % 3;
            color_choice = color_index == 0 ? color_rose : 
                          (color_index == 1 ? color_pistachio : color_lemon);

            turkish_delight_cube(x_pos, y_pos, color_choice);
        }
    }

    // Bottom tray base
    color([0.8, 0.7, 0.6]) {
        translate([0, 0, -cube_size/2 - 3]) {
            cube([total_width + 10, total_width + 10, 3], center = true);
        }
    }

    // Powder sugar dusting on tray
    color(color_powder) {
        translate([0, 0, -cube_size/2 - 2.5]) {
            cube([total_width + 12, total_width + 12, 0.5], center = true);
        }
    }
}

// Main assembly
