    angle = i * 137.5;
    radius = total_width/2 + 5 + i * 0.5;
    color(color_powder) {
        translate([cos(angle) * radius, sin(angle) * radius, -cube_size/2 - 2]) {
            sphere(d = 0.5 + (i % 3) * 0.2);
        }
    }
}

