// Sheer watermelon slice with rind, flesh, and seeds
// Scaled to ~80mm diameter for 3D printing

// Main parameters
watermelon_radius = 40;
watermelon_thickness = 25;
rind_thickness = 4;
seed_count = 18;
seed_radius = 1.2;
seed_length = 3;

// Colors
rind_color = [0.1, 0.6, 0.2];
light_rind_color = [0.8, 0.9, 0.6];
flesh_color = [0.9, 0.25, 0.3];
seed_color = [0.1, 0.1, 0.1];

// Create watermelon slice
module watermelon_slice() {
    // Main flesh body - 1/8 of a sphere
    color(flesh_color)
    intersection() {
        sphere(r = watermelon_radius);
        translate([0, -watermelon_radius, 0])
        cube([watermelon_radius * 2, watermelon_radius, watermelon_thickness], center = true);
    }

    // White rind layer
    color(light_rind_color) {
        difference() {
            // Outer rind sphere
            sphere(r = watermelon_radius);

            // Cut inner sphere for white rind
            translate([0, 0, -watermelon_thickness/2 + rind_thickness])
            sphere(r = watermelon_radius - rind_thickness);

            // Cut slice shape
            translate([0, -watermelon_radius, 0])
            cube([watermelon_radius * 2, watermelon_radius, watermelon_thickness], center = true);
        }
    }

    // Green outer rind
    color(rind_color) {
        difference() {
            sphere(r = watermelon_radius);
            translate([0, 0, -watermelon_thickness/2 + rind_thickness])
            sphere(r = watermelon_radius - rind_thickness);
            translate([0, -watermelon_radius, 0])
            cube([watermelon_radius * 2, watermelon_radius, watermelon_thickness], center = true);
        }
    }
}

// Create seed module
module seed() {
    color(seed_color)
    rotate([90, 0, 0])
    scale([seed_radius, seed_length, seed_radius])
    sphere(r = 1);
}

// Place seeds in realistic pattern
module place_seeds() {
    for (i = [0:seed_count-1]) {
        angle = i * 137.5;  // Golden angle for natural distribution
        radius = 10 + ((i * 2) % 15);
        height = 2 + sin(i * 0.8) * 1.5;

        // Calculate position on curved surface
        x = cos(angle) * radius;
        y = sin(angle) * radius - watermelon_radius/2;
        z = sqrt(watermelon_radius*watermelon_radius - (x*x + y*y)) - watermelon_radius + height;

        // Only place seeds within slice bounds
        if (x > 0 && y > -watermelon_radius/2 && z > -watermelon_thickness/2 + rind_thickness + 1) {
            translate([x, y, z])
            rotate([0, 0, angle + 90])
            seed();
        }
    }
}

// Add subtle texture lines
module flesh_texture() {
    color([flesh_color[0]*0.9, flesh_color[1]*0.9, flesh_color[2]*0.9])
    for (i = [0:5]) {
        rotate([0, 0, i * 15])
        translate([0, watermelon_radius/4, -watermelon_thickness/2 + rind_thickness + 1])
        scale([0.3, watermelon_radius/2, 0.3])
        sphere(r = 1);
    }
}

// Main assembly
rotate([0, -30, 0])
union() {
    watermelon_slice();
    place_seeds();
    flesh_texture();
}

