surface_texture_height = 1.5;

// Garnish parameters
saffron_strands = 12;
almond_count = 8;
pistachio_count = 10;

// Colors
color_bowl = [0.9, 0.9, 0.9];  // Off-white ceramic
color_pudding = [0.98, 0.95, 0.85];  // Creamy off-white
color_saffron = [0.9, 0.5, 0.1];  // Deep orange
color_almond = [0.95, 0.85, 0.7];  // Light beige
color_pistachio = [0.7, 0.85, 0.6];  // Light green

module bowl() {
    // Outer bowl
    color(color_bowl)
    difference() {
        cylinder(h = bowl_height, d = bowl_outer_d, $fn = 64);

        // Hollow out the inside
        translate([0, 0, base_thickness])
        cylinder(h = bowl_height - base_thickness + 1, d = bowl_inner_d, $fn = 64);
    }

    // Add base
    translate([0, 0, -base_thickness])
    cylinder(h = base_thickness, d = bowl_outer_d, $fn = 64);
}

module pudding_surface() {
    color(color_pudding)
    difference() {
        // Main pudding
        translate([0, 0, base_thickness])
        cylinder(h = pudding_height, d = bowl_inner_d - 2, $fn = 64);

        // Scoop out center slightly for realistic serving
        translate([0, 0, base_thickness + pudding_height/2])
        sphere(d = bowl_inner_d - 10, $fn = 32);
    }

    // Add surface texture - small ripples
    for(i = [0:8]) {
        rotate([0, 0, i * 45])
        translate([0, (bowl_inner_d - 10)/2 - 5, base_thickness + pudding_height - surface_texture_height/2])
        scale([1, 0.3, 1])
        sphere(d = 10 + i, $fn = 16);
    }
}

module saffron_strand(x, y, angle) {
    color(color_saffron)
    translate([x, y, base_thickness + pudding_height + 0.1])
    rotate([0, 0, angle])
    scale([1, 0.1, 0.1])
    cylinder(h = 8, d = 1, $fn = 8);
}

module almond_slice(x, y, angle) {
    color(color_almond)
    translate([x, y, base_thickness + pudding_height + 0.2])
    rotate([90, 0, angle])
    scale([1, 0.3, 0.2])
    cylinder(h = 6, d = 4, $fn = 8);
}

module pistachio_piece(x, y, angle) {
    color(color_pistachio)
    translate([x, y, base_thickness + pudding_height + 0.15])
    rotate([0, 0, angle])
    scale([0.8, 0.6, 0.4])
    sphere(d = 3, $fn = 8);
}

// Assembly
bowl();
pudding_surface();

// Add saffron strands
for(i = [0:saffron_strands-1]) {
    angle = i * 360 / saffron_strands;
    radius = (bowl_inner_d - 20) / 2 * (0.5 + sin(i * 30) * 0.3);
    saffron_strand(
        cos(angle) * radius,
        sin(angle) * radius,
        angle + 45
    );
}

// Add almond slices
for(i = [0:almond_count-1]) {
    angle = i * 360 / almond_count + 15;
    radius = (bowl_inner_d - 25) / 2;
    almond_slice(
        cos(angle) * radius,
        sin(angle) * radius,
        angle
    );
}

// Add pistachio pieces
for(i = [0:pistachio_count-1]) {
    angle = i * 360 / pistachio_count + 30;
    radius = (bowl_inner_d - 15) / 2 * 0.7;
    pistachio_piece(
        cos(angle) * radius,
        sin(angle) * radius,
        angle + 90
    );
}

// Kheer (Indian Rice Pudding) - Realistic Style
// A semi-solid creamy dessert with rice grains, nuts and saffron strands

$fn = 32;

// Main color definitions
color_rice = [1, 0.95, 0.85];
color_cream = [0.98, 0.92, 0.82];
color_saffron = [0.95, 0.7, 0.2];
color_cardamom = [0.1, 0.6, 0.1];
color_almond = [0.95, 0.85, 0.7];
color_raisin = [0.4, 0.2, 0.1];

// Main bowl of kheer
module kheer_bowl() {
    difference() {
        // Outer bowl shape
        cylinder(h = 25, r1 = 40, r2 = 35, center = false);

        // Hollow interior
        translate([0, 0, 2])
            cylinder(h = 23, r1 = 37, r2 = 32, center = false);
    }
}

// Creamy kheer base
module kheer_base() {
    color(color_cream) {
        translate([0, 0, 2])
            cylinder(h = 20, r1 = 36, r2 = 31, center = false);
    }
}

// Individual rice grain
module rice_grain(x, y, z) {
    color(color_rice) {
        translate([x, y, z])
            rotate([rand(0, 90), rand(0, 90), rand(0, 90)])
                scale([1, 0.3, 0.2])
                    sphere(r = 1.5);
    }
}

// Generate random rice grains
module rice_grains() {
    for(i = [0:30]) {
        rice_grain(
            rand(-30, 30),
            rand(-30, 30),
            rand(5, 20)
        );
    }
}

// Almond slice
module almond_slice(x, y, z) {
    color(color_almond) {
        translate([x, y, z])
            rotate([90, 0, rand(0, 360)])
                scale([2, 1.5, 0.5])
                    cylinder(h = 0.5, r = 2);
    }
}

// Raisin
module raisin(x, y, z) {
    color(color_raisin) {
        translate([x, y, z])
            scale([1, 0.7, 0.7])
                sphere(r = 1.5);
    }
}

// Saffron strand
module saffron_strand(x, y, z) {
    color(color_saffron) {
        translate([x, y, z])
            rotate([90, 0, rand(0, 360)])
                cylinder(h = 15, r = 0.3, center = false);
    }
}

// Cardamom seed
module cardamom_seed(x, y, z) {
    color(color_cardamom) {
        translate([x, y, z])
            scale([0.8, 0.5, 0.5])
                sphere(r = 0.8);
    }
}

// Generate nuts and raisins
module nuts_and_raisins() {
    for(i = [0:10]) {
        almond_slice(rand(-25, 25), rand(-25, 25), rand(15, 22));
        raisin(rand(-28, 28), rand(-28, 28), rand(12, 20));
    }
}

// Generate saffron strands
module saffron_strands() {
    for(i = [0:5]) {
        saffron_strand(rand(-20, 20), rand(-20, 20), rand(18, 22));
    }
}

// Cardamom seeds scattered on top
module cardamom_seeds() {
    for(i = [0:8]) {
        cardamom_seed(rand(-25, 25), rand(-25, 25), 22);
    }
}

// Cream swirls on top
module cream_swirls() {
    for(i = [0:3]) {
        color(color_cream) {
            translate([rand(-15, 15), rand(-15, 15), 22])
                rotate([0, 0, rand(0, 360)])
                    scale([1, 0.5, 1])
                        cylinder(h = 1, r = rand(5, 8));
        }
    }
}

// Random number generator
function rand(min, max) = min + rands(0, 1, 1)[0] * (max - min);

// Assemble the complete kheer
kheer_bowl();
kheer_base();
rice_grains();
nuts_and_raisins();
saffron_strands();
cardamom_seeds();
cream_swirls();

// Payasam bowl with contents
// Traditional South Indian dessert with rice, milk and garnishes

bowl_height = 25;
bowl_outer_dia = 60;
wall_thickness = 2;
base_thickness = 4;
contents_height = 18;

module bowl() {
    difference() {
        // outer bowl
        cylinder(h = bowl_height, d = bowl_outer_dia, $fn = 60);
        // hollow inside
        translate([0, 0, base_thickness])
            cylinder(h = bowl_height - base_thickness + 1, 
                     d = bowl_outer_dia - 2 * wall_thickness, 
                     $fn = 60);
    }
}

module rice_grain() {
    // single rice grain
    scale([1, 0.4, 0.3])
        sphere(d = 2.5, $fn = 12);
}

module rice_layer() {
    // layer of rice grains in payasam
    for(i = [0:12]) {
        for(j = [0:8]) {
            if(i % 2 == 0) {
                translate([i * 4 - 24, j * 5 - 20, contents_height / 2])
                    rice_grain();
            } else {
                translate([i * 4 - 24, j * 5 - 22.5, contents_height / 2])
                    rice_grain();
            }
        }
    }
}

module cashew() {
    // cashew nut half
    difference() {
        scale([1, 0.4, 0.5])
            sphere(d = 6, $fn = 16);
        translate([0, 0, -0.1])
            cube([7, 7, 1.5], center = true);
    }
}

module raisins() {
    // small raisin
    scale([0.8, 1, 1.2])
        sphere(d = 3, $fn = 8);
}

module garnish() {
    // floating cashews and raisins
    color("goldenrod")
    for(i = [0:5]) {
        rotate([0, 0, i * 60])
            translate([15 + i * 2, 0, contents_height + 2])
                rotate([0, 0, 45])
                    cashew();
    }

    color("saddlebrown")
    for(i = [0:7]) {
        rotate([0, 0, i * 51])
            translate([10 + i * 1.5, 0, contents_height + 1])
                raisins();
    }
}

module payasam_contents() {
    // liquid base
    color("cornsilk")
    translate([0, 0, base_thickness])
        cylinder(h = contents_height, 
                 d = bowl_outer_dia - 2 * wall_thickness - 2, 
                 $fn = 60);

    // rice grains
    color("ivory")
    rice_layer();

    // garnish on top
    garnish();
}

// Main assembly
color("burlywood")
bowl();

translate([0, 0, 0.5])
    payasam_contents();

// Shrikhand - Indian strained yogurt dessert
// Medium complexity realistic style

// Main parameters
$fn = 32;
bowl_diameter = 60;
bowl_height = 35;
shrikhand_height = 25;
saffron_strands = 8;

module bowl() {
    // Outer bowl
    difference() {
        cylinder(h = bowl_height, d1 = bowl_diameter, d2 = bowl_diameter * 0.9);
        translate([0, 0, 2])
            cylinder(h = bowl_height, d1 = bowl_diameter - 4, d2 = bowl_diameter * 0.9 - 4);
    }

    // Bowl base
    translate([0, 0, -1])
        cylinder(h = 3, d = bowl_diameter * 0.7);
}

module shrikhand_surface() {
    // Create textured surface with swirls
    color([1, 0.95, 0.8])
    for(i = [0:5:360]) {
        rotate([0, 0, i])
        translate([i/20, 0, shrikhand_height + sin(i*3)*0.5])
            sphere(d = 3 - i/50);
    }
}

module shrikhand_body() {
    color([1, 0.92, 0.75])
    difference() {
        // Main body - slightly domed
        translate([0, 0, bowl_height - shrikhand_height])
            cylinder(h = shrikhand_height, d1 = bowl_diameter - 6, d2 = bowl_diameter - 8);

        // Create slight depression in center
        translate([0, 0, bowl_height - shrikhand_height + 2])
            sphere(d = 20);
    }
}

module pistachio() {
    color([0.4, 0.7, 0.4])
    scale([1, 0.6, 0.3])
        sphere(d = 3);
}

module almonds() {
    color([0.9, 0.8, 0.6])
    scale([1.2, 0.7, 0.4])
        sphere(d = 4);
}

module saffron_strand() {
    color([0.9, 0.7, 0.2])
    rotate([90, 0, 0])
        cylinder(h = 8, d = 0.3);
}

module garnish() {
    // Pistachios around edge
    for(i = [0:45:360]) {
        rotate([0, 0, i])
        translate([bowl_diameter/2 - 8, 0, bowl_height - shrikhand_height + 1])
            pistachio();
    }

    // Almond slivers
    for(i = [22.5:45:360]) {
        rotate([0, 0, i])
        translate([bowl_diameter/2 - 12, 0, bowl_height - shrikhand_height + 1.5])
            almonds();
    }

    // Saffron strands in center
    for(i = [0:360/saffron_strands:360]) {
        rotate([0, 0, i])
        translate([5, 0, bowl_height - shrikhand_height/2])
            saffron_strand();
    }
}

// Assembly
bowl();
shrikhand_body();
shrikhand_surface();
garnish();

