// Realistic Rasgulla Mishti - Medium Complexity
// Soft cottage cheese balls soaked in sugar syrup

// Main module for complete rasgulla
module rasgulla() {
    color("snow") 
    union() {
        // Main spherical body
        translate([0, 0, 0]) 
        sphere(d=45, $fn=64);

        // Surface texture - small indentations
        for(i = [0:5:355]) {
            rotate([0, 0, i]) 
            translate([22, 0, 5]) 
            rotate([90, 0, 0]) 
            cylinder(h=2, d1=3, d2=1, $fn=16);
        }

        // Second ring of texture
        for(i = [15:10:355]) {
            rotate([0, 0, i]) 
            translate([20, 0, -8]) 
            rotate([90, 0, 0]) 
            cylinder(h=1.5, d1=2.5, d2=1, $fn=12);
        }

        // Bottom flat surface for printing
        translate([0, 0, -22.5]) 
        cylinder(h=1, d=45.5, $fn=64);
    }

    // Syrup droplets on surface
    color("honeydew") 
    for(j = [0:30:330]) {
        a = j + 15;
        rotate([0, 0, a]) 
        translate([18 * cos(30), 18 * sin(30), 20]) 
        sphere(d=1.5, $fn=12);
    }
}

// Create a pair of rasgullas
translate([-30, 0, 0]) rasgulla();
translate([30, 0, 0]) rasgulla();

// Sandesh - Bengali sweet cottage cheese dessert
// Realistic style with medium complexity

// Main module to generate the complete sandesh
module sandesh_piece() {
    // 1. Base diamond shape (main body)
    color("Beige") {
        union() {
            // Main body - diamond shaped with rounded edges
            difference() {
                // Base diamond
                cube([45, 45, 20], center = true);

                // Rounded edges using sphere subtraction
                translate([0, 0, 10]) {
                    sphere(r = 25, $fn = 32);
                }
            }

            // 2. Top surface - slightly curved
            translate([0, 0, 10]) {
                scale([1.1, 1.1, 0.3]) {
                    sphere(r = 20, $fn = 32);
                }
            }
        }
    }

    // 3. Decorative patterns - traditional concentric circles
    color("Ivory") {
        for(i = [1:3]) {
            translate([0, 0, 15 + i*0.5]) {
                cylinder(h = 0.5, r = 20 - i*5, $fn = 32);
            }
        }
    }

    // 4. Surface texture - small indentations
    color("AntiqueWhite") {
        for(x = [-20:10:20]) {
            for(y = [-20:10:20]) {
                if(abs(x) + abs(y) < 25) {
                    translate([x, y, 16]) {
                        rotate([0, 180, 0]) {
                            cylinder(h = 1, r1 = 1.5, r2 = 0.5, $fn = 12);
                        }
                    }
                }
            }
        }
    }

    // 5. Pistachio garnish pieces
    color("DarkGreen") {
        for(i = [1:5]) {
            angle = i * 72;
            rotate([0, 0, angle]) {
                translate([0, 15, 17]) {
                    scale([1, 1.5, 0.3]) {
                        sphere(r = 2, $fn = 8);
                    }
                }
            }
        }
    }

    // 6. Saffron strands
    color("DarkOrange") {
        for(i = [1:4]) {
            angle = i * 90 + 45;
            rotate([0, 0, angle]) {
                translate([0, 10, 17.5]) {
                    rotate([45, 0, 0]) {
                        cylinder(h = 8, r1 = 0.5, r2 = 0.1, $fn = 6);
                    }
                }
            }
        }
    }
}

// Generate the sandesh piece
sandesh_piece();

