// Malpua - Indian sweet pancake dessert
// Realistic style with medium complexity

// Color definitions
$fa = 1;
$fs = 0.4;

// Main malpua module
module malpua() {
    // Base pancake body
    color("#D2691E") 
    difference() {
        // Main disc shape
        scale([1, 0.9, 0.3]) 
        sphere(d=60, $fn=100);

        // Center depression
        translate([0, 0, -1])
        scale([1, 1, 0.5])
        sphere(d=40, $fn=100);
    }

    // Rolled edges
    color("#CD853F")
    rotate_extrude($fn=100)
    translate([28, 0, 0])
    rotate([0, 90, 0])
    circle(d=8, $fn=20);

    // Surface texture - bubbles
    for(i = [0:15]) {
        angle = i * 24 + (i % 3) * 15;
        radius = 20 + (i % 4) * 5;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            8 + sin(i * 45) * 0.8
        ])
        color("#8B4513")
        sphere(d=3 + (i % 3) * 1.5);
    }

    // Sugar syrup pools
    color("#FFD700", 0.7)
    for(i = [0:8]) {
        angle = i * 45;
        radius = 15 + (i % 2) * 10;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            9
        ])
        scale([1, 1, 0.1])
        sphere(d=6 + (i % 3) * 2);
    }

    // Garnish - pistachio pieces
    color("#228B22")
    for(i = [0:12]) {
        angle = i * 30;
        radius = 22 + (i % 3) * 8;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            10
        ])
        rotate([0, 0, angle])
        scale([1, 0.3, 0.1])
        cube([4, 2, 0.5], center=true);
    }

    // Garnish - almond slivers
    color("#DEB887")
    for(i = [0:10]) {
        angle = i * 36 + 15;
        radius = 18 + (i % 4) * 7;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            10.2
        ])
        rotate([0, 0, angle + 45])
        scale([0.1, 1, 0.1])
        cylinder(h=4, d=1.5);
    }

    // Cardamom seeds
    color("#8B7355")
    for(i = [0:20]) {
        angle = i * 18 + (i % 2) * 9;
        radius = 8 + (i % 5) * 6;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            9.5
        ])
        sphere(d=0.8);
    }
}

// Create the malpua
translate([0, 0, 5])
malpua();

// Realistic Modak (Indian Sweet Dumpling)
// Medium complexity 3D printable model

// Main parameters
$fn = 50;
modak_height = 65;
base_width = 35;
top_knob_height = 8;
texture_depth = 1.5;
wall_thickness = 2;

// Colors
