jaggery_color = [0.7, 0.4, 0.2];
coconut_color = [0.9, 0.85, 0.7];

// Main modak assembly
module modak() {
    color(rice_color) {
        union() {
            // Base hemisphere
            translate([0, 0, modak_height/2 - 5])
                scale([1, 1, 0.7])
                sphere(d = base_width);

            // Middle section with pleated texture
            pleated_middle();

            // Top cone
            translate([0, 0, modak_height - top_knob_height - 10])
                cylinder(h1 = 20, h2 = 0, r1 = base_width/2 - 5, r2 = 0);

            // Top knob
            translate([0, 0, modak_height - top_knob_height])
                sphere(d = top_knob_height * 2);
        }
    }

    // Filling visible through opening
    color(jaggery_color)
    translate([0, 0, 10])
        sphere(d = base_width - 10);

    // Coconut flakes on top
    color(coconut_color)
    coconut_topping();
}

// Create pleated middle section
module pleated_middle() {
    for(i = [0:15:359]) {
        rotate([0, 0, i])
        translate([base_width/2 - 5, 0, 20])
            rotate([90, 0, 0])
            scale([1, 0.3, 1])
            cylinder(h = 25, r = 3);
    }
}

// Add coconut topping texture
module coconut_topping() {
    for(i = [0:30:359]) {
        rotate([0, 0, i])
        translate([0, base_width/3, modak_height - 5])
            scale([0.5, 0.5, 0.1])
            sphere(d = 3);
    }

    for(i = [15:30:359]) {
        rotate([0, 0, i])
        translate([0, base_width/4, modak_height - 3])
            scale([0.4, 0.4, 0.1])
            sphere(d = 2);
    }
}

// Create base platform for printing
module base_platform() {
    color([0.9, 0.9, 0.9])
    translate([0, 0, -1])
        cylinder(h = 2, r = base_width/2 + 5);
}

// Assembly
base_platform();
modak();

// Mysorepak - South Indian ghee sweet with porous texture
// Dimensions: 75mm x 50mm x 25mm (printable size)

// Main parameters
bar_length = 75;
bar_width = 50;
bar_height = 25;
crumb_size = 1.5;
crumb_density = 0.7;
layer_count = 8;

// Color definitions
ghee_color = [1.0, 0.7, 0.3];  // Golden yellow
dark_ghee_color = [0.9, 0.6, 0.2];
burnt_edge_color = [0.7, 0.4, 0.1];

// Create porous crumb texture
module crumb(x, y, z) {
    translate([x, y, z])
        sphere(crumb_size * (0.7 + rand() * 0.6), $fn=8);
}

// Generate random number between 0 and 1
function rand(n=0) = rands(0, 1, 1, n)[0];

// Create crumb layer with holes
module crumb_layer(height) {
    difference() {
        // Solid layer
        color(ghee_color)
            cube([bar_length, bar_width, 2], center=true);

        // Add random holes for porous texture
        for (i = [0:50]) {
            x = (rand(i*3) - 0.5) * bar_length;
            y = (rand(i*7) - 0.5) * bar_width;
            z = height + (rand(i*11) - 0.5);
            translate([x, y, z])
                sphere(crumb_size * 2, $fn=6);
        }
    }
}

// Main mysorepak bar
module mysorepak_bar() {
    // Base shape with rounded edges
    difference() {
        // Main bar slightly tapered
        color(ghee_color)
            scale([1, 1, 1])
                cube([bar_length, bar_width, bar_height], center=true);

        // Chamfer bottom edges
        translate([0, 0, -bar_height/2 - 0.1])
            cube([bar_length + 2, bar_width + 2, 2], center=true);

        // Add chamfers on sides
        for (i = [-1, 1]) {
            translate([i * bar_length/2, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h=bar_width+2, r=2, center=true);
        }
    }

    // Add porous layers
    for (layer = [0:layer_count-1]) {
        z_pos = -bar_height/2 + 2 + layer * (bar_height - 4) / layer_count;
        translate([0, 0, z_pos])
            crumb_layer(z_pos);
    }

    // Surface texture bumps
    for (i = [0:200]) {
        x = (rand(i*13) - 0.5) * (bar_length - 4);
        y = (rand(i*17) - 0.5) * (bar_width - 4);
        z = bar_height/2 - 0.5;

        if (abs(x) < bar_length/2 - 2 && abs(y) < bar_width/2 - 2) {
            translate([x, y, z])
                color(dark_ghee_color)
                    sphere(crumb_size * 0.6, $fn=6);
        }
    }

    // Burnt edges for authenticity
    edge_width = 2;

    // Top edge browning
    translate([0, 0, bar_height/2 - 0.5])
        color(burnt_edge_color)
            cube([bar_length + 0.5, bar_width + 0.5, 1], center=true);

    // Side edge browning
    for (side = [-1, 1]) {
        translate([side * (bar_length/2 - 0.5), 0, 0])
            color(burnt_edge_color)
                cube([1, bar_width + 0.5, bar_height], center=true);
    }
}

// Create the complete mysorepak
mysorepak_bar();

