// Main petha body - cylindrical with slight taper and rounded top
module petha_body() {
    // Outer translucent white body
    color([1, 1, 0.95, 0.6]) {
        // Main cylindrical base with slight taper
        cylinder(h = petha_height * 0.7, r1 = petha_radius, r2 = petha_radius * 0.95, center = true);

        // Rounded top dome
        translate([0, 0, petha_height * 0.35])
            scale([1, 1, 0.7])
                sphere(r = petha_radius * 0.95);
    }

    // Inner hollow space for printing
    color([1, 1, 0.95, 0.6]) {
        difference() {
            cylinder(h = petha_height * 0.7, r1 = petha_radius - wall_thickness, 
                    r2 = (petha_radius * 0.95) - wall_thickness, center = true);
            translate([0, 0, petha_height * 0.35])
                scale([1, 1, 0.7])
                    sphere(r = (petha_radius * 0.95) - wall_thickness);
        }
    }
}

// Sugar syrup texture on surface
module syrup_coating() {
    color([1, 1, 0.85, 0.4]) {
        for(i = [0:15]) {
            rotate([0, 0, i * 24])
                translate([petha_radius * 0.7, 0, petha_height * 0.2])
                    cube([petha_radius * 0.3, 0.5, 1], center = true);
        }

        // Random syrup droplets
        for(i = [0:5]) {
            translate([
                petha_radius * 0.8 * cos(i * 60),
                petha_radius * 0.8 * sin(i * 60),
                petha_height * 0.3
            ])
                sphere(r = 1.5);
        }
    }
}

// Petha seeds inside
module seeds() {
    color([0.4, 0.2, 0.1]) {
        for(i = [0:seed_count-1]) {
            angle = i * (360 / seed_count);
            radius = petha_radius * 0.5;
            translate([
                radius * cos(angle),
                radius * sin(angle),
                petha_height * 0.1
            ]) {
                scale([1, 1.5, 0.5])
                    sphere(r = 2);
            }
        }
    }
}

// Surface texture lines
module surface_texture() {
    color([1, 1, 0.9, 0.3]) {
        // Vertical grooves
        for(i = [0:17]) {
            rotate([0, 0, i * 20])
                translate([petha_radius - 0.5, 0, 0])
                    cube([1, petha_radius * 0.1, petha_height * 0.8], center = true);
        }

        // Horizontal rings
        for(h = [0.2:0.2:0.8]) {
            translate([0, 0, petha_height * h - petha_height * 0.5])
                rotate_extrude()
                    translate([petha_radius - 0.5, 0])
                        circle(r = 0.5);
        }
    }
}

// Assemble the complete petha
union() {
    petha_body();
    syrup_coating();
    seeds();
    surface_texture();
}

