wafer_thickness = 0.8;
ridge_height = 0.3;
ridge_spacing = 2;

// Main cookie module
module barquillo() {
    // Outer shell with wafer texture
    difference() {
        // Main tube
        rotate([90, 0, 0])
        cylinder(h=cookie_length, d=cookie_diameter, center=true);

        // Hollow center
        rotate([90, 0, 0])
        cylinder(h=cookie_length + 2, d=cookie_diameter - 2*wall_thickness, center=true);
    }

    // Add wafer ridges on outer surface
    for (i = [0:ridge_spacing:cookie_length]) {
        translate([0, 0, i - cookie_length/2])
        rotate([90, 0, 0])
        rotate_extrude(angle=360, $fn=100)
        translate([cookie_diameter/2, 0, 0])
        square([ridge_height, wafer_thickness]);
    }

    // Cross-hatch pattern
    for (angle = [0:30:150]) {
        rotate([0, 0, angle])
        for (z = [-cookie_length/2:ridge_spacing:cookie_length/2]) {
            translate([0, 0, z])
            rotate([90, 0, 0])
            cylinder(h=ridge_height*2, d=0.4, center=true);
        }
    }

    // Slightly flattened ends from rolling
    translate([0, 0, cookie_length/2])
    rotate([90, 0, 0])
    scale([1, 1, 0.8])
    cylinder(h=2, d=cookie_diameter, center=true);

    translate([0, 0, -cookie_length/2])
    rotate([90, 0, 0])
    scale([1, 1, 0.8])
    cylinder(h=2, d=cookie_diameter, center=true);

    // Golden-brown coloring variation
    for (i = [0:5:cookie_length-5]) {
        translate([0, 0, i - cookie_length/2])
        rotate([90, 0, 0])
        color("burlywood")
        scale([1 + sin(i*3)*0.02, 1 + cos(i*3)*0.02, 1])
        cylinder(h=2, d=cookie_diameter, center=true);
    }
}

