sugar_color = [0.98, 0.97, 0.95];  // White sugar coating

// Module for the main pastry ring
module pastry_ring() {
    difference() {
        // Outer torus shape
        rotate_extrude(angle = 360, $fn = 64)
            translate([rosquilla_outer_diameter/2 - rosquilla_thickness/2, 0, 0])
                circle(r = rosquilla_thickness/2, $fn = 32);

        // Inner hole
        rotate_extrude(angle = 360, $fn = 64)
            translate([rosquilla_inner_diameter/2 + rosquilla_thickness/2, 0, 0])
                circle(r = rosquilla_thickness/2, $fn = 32);
    }
}

// Module for creating ridges around the ring
module pastry_ridges() {
    ridge_height = 1.5;
    ridge_width = 3;
    num_ridges = 36;

    for(i = [0:num_ridges-1]) {
        rotate([0, 0, i * 360/num_ridges])
            translate([rosquilla_outer_diameter/2 - rosquilla_thickness/2, 0, 0])
                rotate([90, 0, 0])
                    linear_extrude(height = rosquilla_thickness)
                        hull() {
                            translate([-ridge_width/2, -rosquilla_thickness/2 + 2, 0])
                                circle(r = ridge_width/4, $fn = 8);
                            translate([ridge_width/2, -rosquilla_thickness/2 + 2, 0])
                                circle(r = ridge_width/4, $fn = 8);
                        }
    }
}

// Module for sugar crystals on surface
module sugar_coating() {
    crystal_size = 0.4;
    num_crystals = 200;

    // Random seed for consistent crystal placement
    seed = 42;

    for(i = [0:num_crystals-1]) {
        angle = (i * 137.5 + seed) % 360;
        radius = rosquilla_outer_diameter/2 - rosquilla_thickness/2 + 
                 (sin(i * 0.5) * rosquilla_thickness/4);

        rotate([0, 0, angle])
            translate([radius, 0, rosquilla_thickness/2 - 1])
                scale([1, 1, 0.7])
                    sphere(r = crystal_size, $fn = 6);
    }
}

// Module for subtle imperfections to make it look handmade
module imperfections() {
    for(i = [0:5]) {
        angle = i * 60 + 15;
        radius = rosquilla_outer_diameter/2 - rosquilla_thickness/2 + 
                 (cos(i * 2) * 3);

        rotate([0, 0, angle])
            translate([radius, 0, rosquilla_thickness/2])
                scale([1.2, 1.2, 0.8])
                    sphere(r = 1.5, $fn = 8);
    }
}

// Main assembly
union() {
    // Base pastry
    color(pastry_color)
        union() {
            pastry_ring();
            pastry_ridges();
            imperfections();
        }

    // Sugar coating
    color(sugar_color)
        sugar_coating();
}

