$fn = 48;

// Main dimensions (realistic polvoron size)
module_size = 60;  // 60mm diameter for 3D printing

// Polvoron body - slightly tapered cylinder
module polvoron_body(height = 20, top_diameter = module_size, bottom_diameter = module_size * 0.9) {
    difference() {
        // Main tapered cylinder
        cylinder(h = height, r1 = bottom_diameter/2, r2 = top_diameter/2, $fn = 64);

        // Hollow center for realistic thickness (3mm walls)
        translate([0, 0, 3])
            cylinder(h = height - 6, r1 = bottom_diameter/2 - 3, r2 = top_diameter/2 - 3, $fn = 64);
    }
}

// Textured top surface - creates the pressed/patterned look
module textured_top() {
    // Base indentation
    translate([0, 0, 20])
        difference() {
            cylinder(h = 2, r = module_size/2 * 0.9, $fn = 64);

            // Center deeper impression
            translate([0, 0, -0.5])
                cylinder(h = 3, r = module_size/4, $fn = 32);
        }

    // Radial texture lines
    for (i = [0:15]) {
        rotate([0, 0, i * 22.5])
            translate([0, module_size/4, 20])
                cube([1, module_size/4, 1], center = true);
    }

    // Small dimples for sugar crystals
    for (i = [0:23]) {
        angle = i * 15;
        radius = module_size/3 * (0.5 + 0.3 * sin(i * 3));
        translate([radius * cos(angle), radius * sin(angle), 21])
            sphere(r = 0.5);
    }
}

// Wrapper paper - crimped edges
module wrapper() {
    color([0.95, 0.9, 0.85])  // Off-white paper color
    difference() {
        // Main wrapper cylinder
        cylinder(h = 22, r = module_size/2 + 3, $fn = 64);

        // Cut out center for polvoron
        translate([0, 0, -1])
            cylinder(h = 25, r = module_size/2 + 1, $fn = 64);
    }

    // Crimped edges
    for (i = [0:35]) {
        rotate([0, 0, i * 10])
            translate([module_size/2 + 3, 0, 0])
                rotate([90, 0, 0])
                    cylinder(h = 8, r = 1, $fn = 8);
    }
}

// Crumbs and texture details
module surface_details() {
    // Random crumbs on surface
    for (i = [0:40]) {
        translate([
            (random(i) - 0.5) * module_size * 0.4,
            (random(i + 100) - 0.5) * module_size * 0.4,
            20 + random(i + 200) * 0.5
        ])
            sphere(r = 0.3 + random(i + 300) * 0.5);
    }
}

// Helper function for pseudo-random values
function random(seed) = (seed * 9301 + 49297) % 233280 / 233280;

// Main assembly
union() {
    // Wrapper bottom
    translate([0, 0, -1])
        wrapper();

    // Main polvoron
    color([0.92, 0.78, 0.58])  // Golden toasted color
    polvoron_body();

    // Textured top
    color([0.88, 0.72, 0.52])  // Slightly darker toasted areas
    textured_top();

    // Surface details
    color([0.95, 0.82, 0.65])  // Light powdery color
    surface_details();

    // Wrapper top edges
    translate([0, 0, 20])
        wrapper();
}

// Alfajor - Argentine dulce de leche cookie sandwich
// Dimensions: 50mm diameter, 25mm total height

$fn = 32;

// Main colors
cookie_color = [150/255, 110/255, 80/255];
dulce_color = [160/255, 90/255, 50/255];
coconut_color = [240/255, 240/255, 230/255];

// Main alfajor module
module alfajor() {
    // Bottom cookie
    translate([0, 0, 0])
        cookie();

    // Dulce de leche filling
    translate([0, 0, 4])
        filling();

    // Top cookie
    translate([0, 0, 21])
        rotate([0, 0, 180])
            cookie();

    // Coconut coating around the sides
    coconut_coating();
}

// Cookie with rounded edges and texture
module cookie() {
    color(cookie_color) {
        // Main cookie body
        cylinder(h = 4, r = 25);

        // Rounded top edge
        translate([0, 0, 4])
            rotate_extrude()
                translate([25, 0])
                    circle(r = 1);

        // Rounded bottom edge
        translate([0, 0, 0])
            rotate_extrude()
                translate([25, 0])
                    circle(r = 1);

        // Small holes for texture (repeated pattern)
        for(i = [0:5:355]) {
            rotate([0, 0, i])
                translate([20, 0, 2])
                    cylinder(h = 1, r = 1.5);
        }
    }
}

// Dulce de leche filling with slight overflow
module filling() {
    color(dulce_color) {
        // Main filling layer
        cylinder(h = 17, r = 23);

        // Create slight overflow effect
        for(i = [0:30:359]) {
            rotate([0, 0, i])
                translate([23, 0, 0])
                    cylinder(h = 17, r = 2);
        }
    }
}

// Coconut flakes coating the sides
module coconut_coating() {
    color(coconut_color) {
        difference() {
            // Main coating ring
            cylinder(h = 25, r = 27);
            cylinder(h = 25.5, r = 24);

            // Remove top and bottom to leave only sides
            translate([0, 0, -0.5])
                cube([100, 100, 1], center = true);
            translate([0, 0, 25])
                cube([100, 100, 1], center = true);
        }

        // Add texture with small bumps for coconut flakes
        for(angle = [0:15:359]) {
            for(h = [2:2:23]) {
                rotate([0, 0, angle])
                    translate([26, 0, h])
                        sphere(r = 0.8);
            }
        }
    }
}

// Render the complete alfajor
alfajor();

// Chaja - Uruguayan meringue cake
// Layers: sponge cake, dulce de leche, meringue, covered in chocolate glaze

$fn = 24;

// Main dimensions
chaja_width = 60;
chaja_length = 45;
chaja_height = 35;

// Thicknesses
cake_thickness = 8;
dulce_thickness = 6;
meringue_thickness = 15;
chocolate_thickness = 3;

// Colors
color_cake = [0.82, 0.71, 0.55];
color_dulce = [0.72, 0.52, 0.38];
color_meringue = [0.98, 0.95, 0.90];
color_chocolate = [0.36, 0.25, 0.20];

module rounded_cube(w, l, h, r) {
    // Create a rounded cube with given dimensions and corner radius
    translate([r, r, 0])
    minkowski() {
        cube([w-2*r, l-2*r, h]);
        cylinder(h=h, r=r);
    }
}

module sponge_layer() {
    // Base sponge cake layer with rounded edges
    color(color_cake)
    rounded_cube(chaja_width, chaja_length, cake_thickness, 5);
}

module dulce_layer() {
    // Dulce de leche layer slightly smaller than base
    color(color_dulce)
    translate([0, 0, cake_thickness])
    rounded_cube(chaja_width-4, chaja_length-4, dulce_thickness, 4);
}

module meringue_peaks() {
    // Create individual meringue peaks
    module peak(x, y) {
        translate([x, y, cake_thickness + dulce_thickness + 2])
        scale([1, 1, 0.8])
        sphere(d = 6 + rand*4);
    }

    // Random seed for varied peaks
    seed = 42;

    // Generate meringue peaks across top
    for (i = [-2:2]) {
        for (j = [-1:1]) {
            rand = (i*i + j*j + seed) % 10 / 10.0;
            peak(i*10 + rand*3 - 5, j*10 + rand*3);
        }
    }
}

module meringue_base() {
    // Base meringue layer with peaks on top
    color(color_meringue)
    union() {
        translate([0, 0, cake_thickness + dulce_thickness])
        rounded_cube(chaja_width-2, chaja_length-2, meringue_thickness/3, 3);

        // Add meringue peaks
        meringue_peaks();
    }
}

module chocolate_coating() {
    // Chocolate glaze coating the entire cake
    color(color_chocolate)
    hull() {
        // Bottom coating
        translate([0, 0, -0.5])
        rounded_cube(chaja_width+2, chaja_length+2, 1, 6);

        // Top coating following meringue shape
        translate([0, 0, cake_thickness + dulce_thickness + meringue_thickness + 2])
        rounded_cube(chaja_width, chaja_length, 2, 4);
    }

    // Side coating
    difference() {
        rounded_cube(chaja_width+2, chaja_length+2, 
                     cake_thickness + dulce_thickness + meringue_thickness + 3, 6);
        rounded_cube(chaja_width, chaja_length, 
                     cake_thickness + dulce_thickness + meringue_thickness + 4, 4);
    }
}

module cake_texture() {
    // Add sponge texture with small holes
    color(color_cake)
    for (i = [1:5:chaja_width-10]) {
        for (j = [1:5:chaja_length-10]) {
            translate([i - chaja_width/2, j - chaja_length/2, cake_thickness/2])
            sphere(d = 1.5);
        }
    }
}

// Assemble the chaja
union() {
    // Main cake layers
    sponge_layer();
    dulce_layer();
    meringue_base();
    chocolate_coating();

    // Texture details
    cake_texture();

    // Decorative chocolate drizzle on top
    color(color_chocolate)
    for (i = [1:3]) {
        rotate([0, 0, i*60])
        translate([chaja_width/4 - 10, chaja_length/4 - 5, 
                  cake_thickness + dulce_thickness + meringue_thickness + 4])
        scale([0.3, 3, 0.3])
        cylinder(h = 2, r1 = 1, r2 = 0.5);
    }
}

