// Peach Cobbler 3D Model
// Realistic style, medium complexity
// Overall dimensions: ~80mm diameter x 35mm height

// Color definitions
crust_color = [210/255, 180/255, 140/255];
peach_color = [255/255, 165/255, 100/255];
peach_dark = [220/255, 140/255, 80/255];
juice_color = [255/255, 140/255, 90/255];

// Main baking dish base
module baking_dish() {
    difference() {
        // Outer dish
        cylinder(h=12, r1=40, r2=42, $fn=64);

        // Inner cavity
        translate([0, 0, -1])
            cylinder(h=14, r1=36, r2=38, $fn=64);
    }
}

// Cobbler crust layer
module crust_base() {
    color(crust_color)
    difference() {
        // Main crust in dish
        cylinder(h=8, r1=35, r2=36, $fn=64);

        // Slightly hollow center for peaches
        translate([0, 0, 2])
            cylinder(h=8, r1=28, r2=30, $fn=64);
    }
}

// Peach slice module
module peach_slice(x, y, z, rot, scale_factor) {
    translate([x, y, z]) {
        rotate([0, 0, rot]) {
            scale([scale_factor, scale_factor, scale_factor]) {
                // Peach flesh
                color(peach_color)
                union() {
                    sphere(r=5, $fn=16);

                    // Crescent shape for slice
                    translate([0, -3, 0])
                        cube([12, 6, 10], center=true);
                }

                // Peach skin on edge
                color(peach_dark)
                translate([5, 0, 0])
                    rotate([90, 0, 0])
                        cylinder(h=10, r1=5, r2=5, $fn=16);

                // Center indentation
                color(peach_dark)
                translate([-3, 0, 0])
                    sphere(r=2, $fn=12);

                // Texture lines
                for(i = [-1, 0, 1]) {
                    color(peach_dark)
                    translate([0, i, 0])
                        scale([1, 0.1, 0.1])
                            sphere(r=5, $fn=12);
                }
            }
        }
    }
}

// Generate multiple peach slices
module peach_slices() {
    for(i = [0:11]) {
        angle = i * 30;
        radius = 15 + sin(i * 20) * 5;
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        rot = angle + 90;
        scale_factor = 0.6 + sin(i * 15) * 0.2;
        peach_slice(x, y, 8, rot, scale_factor);
    }
}

// Crust topping pieces
module crust_topping() {
    color(crust_color)
    for(i = [0:8]) {
        angle = i * 40;
        radius = 10 + (i % 3) * 8;
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        translate([x, y, 10 + sin(i * 5) * 2])
            scale([0.8 + sin(i * 10) * 0.3, 0.8 + cos(i * 10) * 0.3, 0.5])
                sphere(r=5, $fn=8);
    }
}

// Juice bubbles
module juice_bubbles() {
    color(juice_color)
    for(i = [0:15]) {
        angle = i * 24;
        radius = 10 + (i % 4) * 5;
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        translate([x, y, 8 + sin(i * 3) * 1.5])
            sphere(r=0.8 + sin(i * 8) * 0.5, $fn=8);
    }
}

// Assemble the peach cobbler
union() {
    // Baking dish
    color([200/255, 200/255, 200/255])
    baking_dish();

    // Crust base in dish
    translate([0, 0, 4])
        crust_base();

    // Peach slices
    peach_slices();

    // Juice between peaches
    juice_bubbles();

    // Crust topping pieces
    crust_topping();

    // Top crust edge
    color(crust_color)
    translate([0, 0, 10])
        cylinder(h=2, r1=36, r2=34, $fn=64);
}

// Blueberry Grunt - Traditional Maritime dessert
// A biscuit-topped blueberry cobbler with berry filling

// Overall dimensions: 80mm diameter, 45mm height
$fa = 2;
$fs = 0.5;

module blueberry_grunt() {
    // Main serving bowl - stoneware style
    color([0.85, 0.85, 0.85]) 
    difference() {
        cylinder(h = 25, r1 = 40, r2 = 38);
        translate([0, 0, 2]) 
        cylinder(h = 25, r1 = 36, r2 = 34);
    }

    // Blueberry filling
    color([0.2, 0.15, 0.4]) 
    translate([0, 0, 5]) {
        difference() {
            cylinder(h = 12, r = 36);
            // Scoop out center for realistic filling
            translate([0, 0, 10]) 
            sphere(r = 38);
        }

        // Individual blueberries
        for(i = [0:19]) {
            rotate([0, 0, i * 18]) 
            translate([rand * 25, rand * 25, 5 + rand * 8]) 
            scale([1, 1, 1.2]) 
            color([0.1, 0.05, 0.35]) 
            sphere(r = 3 + rand * 2);
        }
    }

    // Drop biscuits on top
    module biscuit() {
        color([0.95, 0.8, 0.6]) 
        difference() {
            // Main biscuit shape
            scale([1, 1, 0.6]) 
            sphere(r = 12);

            // Steam vents
            for(i = [0:5]) {
                rotate([0, 0, i * 60]) 
                translate([8, 0, 0]) 
                cylinder(h = 15, r = 1.5);
            }
        }

        // Biscuit top texture
        color([0.9, 0.75, 0.55]) 
        for(i = [0:15]) {
            rotate([0, 0, i * 24]) 
            translate([rand * 8, rand * 8, 5]) 
            sphere(r = 0.5);
        }
    }

    // Place 5 biscuits in a ring pattern
    for(i = [0:4]) {
        rotate([0, 0, i * 72]) 
        translate([0, 20, 15]) 
        biscuit();
    }

    // Biscuit gaps filled with blueberry juice
    color([0.25, 0.1, 0.3, 0.7]) 
    for(i = [0:4]) {
        rotate([0, 0, i * 72 + 36]) 
        translate([0, 15, 15]) 
        cylinder(h = 3, r = 8);
    }

    // Rim decoration - sugared edge
    color([0.9, 0.9, 0.9]) 
    translate([0, 0, 23]) {
        difference() {
            cylinder(h = 2, r1 = 41, r2 = 40);
            cylinder(h = 2, r1 = 39, r2 = 38);
        }

        // Sugar crystals
        for(i = [0:30]) {
            rotate([0, 0, i * 12]) 
            translate([40, 0, 24.5]) 
            scale([0.2, 1, 0.2]) 
            sphere(r = 0.5);
        }
    }
}

// Generate the complete dessert
blueberry_grunt();

