// Main clafoutis base
module clafoutis_base() {
    difference() {
        // Main batter body - slightly domed
        cylinder(h = clafoutis_height, r = clafoutis_diameter/2, $fn = 64);

        // Hollow out the inside
        translate([0, 0, wall_thickness])
            cylinder(h = clafoutis_height - wall_thickness, r = clafoutis_diameter/2 - wall_thickness, $fn = 64);

        // Create rounded top edge
        translate([0, 0, clafoutis_height - 2])
            cylinder(h = 4, r1 = clafoutis_diameter/2 - wall_thickness, r2 = clafoutis_diameter/2, $fn = 64);
    }
}

// Individual cherry with realistic details
module cherry() {
    union() {
        // Main cherry body
        color(cherry_color)
        sphere(d = cherry_diameter, $fn = 32);

        // Stem
        translate([0, 0, cherry_diameter/2])
        color([0.2, 0.4, 0.1])
        rotate([90, 0, 0])
        cylinder(h = 8, r1 = 1, r2 = 0.5, $fn = 8);

        // Highlight on cherry surface
        translate([cherry_diameter/4, cherry_diameter/4, cherry_diameter/3])
        color([0.7, 0.2, 0.3])
        sphere(d = 2, $fn = 16);
    }
}

// Surface texture for baked appearance
module surface_texture() {
    // Random tiny spheres for sugar crystallization effect
    for(i = [0:30]) {
        angle = i * 137.5; // Golden angle for natural distribution
        radius = clafoutis_diameter/2 * 0.9 * sqrt(i/30);
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        translate([x, y, clafoutis_height - 0.5])
        color(browned_color)
        sphere(d = 0.5 + rand(i) * 0.5, $fn = 8);
    }
}

// Helper function for pseudo-random
function rand(seed) = (seed * 9301 + 49297) % 233280 / 233280;

// Main assembly
union() {
    // Baked batter base
    color(batter_color)
    clafoutis_base();

    // Browning on top surface
    translate([0, 0, clafoutis_height - 0.5])
    color(browned_color)
    cylinder(h = 0.5, r = clafoutis_diameter/2, $fn = 64);

    // Place cherries in circular pattern
    for(i = [0:cherry_count-1]) {
        angle = i * 360/cherry_count;
        radius = clafoutis_diameter/3;
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        translate([x, y, clafoutis_height + cherry_depth])
        rotate([0, 0, rand(i * 100) * 30])
        cherry();
    }

    // Add surface texture
    surface_texture();

    // Central cherry
    translate([0, 0, clafoutis_height + cherry_depth])
    cherry();
}

