// German Streuselkuchen (coffee-cake) with crumbs and fruit topping
// Size: 80 mm diameter x 30 mm high â€“ 3D-print friendly

$fa = 2; $fs = 0.5;

// --- Colors ----------------------------------------------------------
crust_col   = "#C19A6B";   // baked yeast base
crumb_col   = "#D2B48C";   // light streusel crumbs
fruit_col   = "#8B0000";   // dark cherry topping

// --- Helper modules --------------------------------------------------
module rounded_cylinder(h, r1, r2, center=false) {
    intersection() {
        cylinder(h=h, r1=r1, r2=r2, center=center);
        sphere(r=r1);
        translate([0,0,h]) sphere(r=r2);
    }
}

// --- Crumb -----------------------------------------------------------
module crumb(r) {
    // Random irregular crumb grain
    translate([rands(-1,1,1)[0],
               rands(-1,1,1)[0],
               rands(-1,1,1)[0]])
    scale([rands(0.8,1.2,1)[0],
           rands(0.8,1.2,1)[0],
           rands(0.8,1.2,1)[0]])
    sphere(r=r);
}

// --- Streusel layer --------------------------------------------------
module streusel_layer(diam, h) {
    difference() {
        // Overall crumb blanket
        color(crumb_col)
        cylinder(h=h, d=diam, $fn=64);

        // Indent for fruit filling
        translate([0,0,-0.1])
        cylinder(h=h+0.2, d=diam*0.55, $fn=64);
    }

    // Individual crumbs on top
    for(i=[0:250]) {
        color(crumb_col)
        translate([rands(-diam/2.1, diam/2.1, 1)[0],
                   rands(-diam/2.1, diam/2.1, 1)[0],
                   h+rands(-0.3,0.3,1)[0]])
        crumb(rands(0.8,1.5,1)[0]);
    }
}

// --- Fruit topping ---------------------------------------------------
module fruit_topping(diam, h) {
    color(fruit_col)
    for(i=[0:45]) {
        rotate([0,0,rands(0,360,1)[0]])
        translate([rands(0,diam*0.23,1)[0],0,0])
        sphere(r=rands(1.2,2.2,1)[0]);
    }
}

// --- Yeast base ------------------------------------------------------
module yeast_base(diam, h) {
    color(crust_col)
    difference() {
        // Main disk
        cylinder(h=h, d=diam, $fn=128);

        // Shallow rim
        translate([0,0,-0.1])
        cylinder(h=h+0.2, d=diam*0.85, $fn=128);
    }

    // Subtle texture lines
    for(a=[0:10:360])
        rotate([0,0,a])
        translate([diam/2-0.5,h/2,0])
        rotate([90,0,90])
        color(crust_col)
        cylinder(h=diam, d=0.3, $fn=8);
}

// --- Assembly --------------------------------------------------------
union() {
    // Bottom to top
    yeast_base(80, 12);
    translate([0,0,12]) fruit_topping(80, 3);
    translate([0,0,12]) streusel_layer(80, 8);
}

// Streusel - German crumb topping for coffee cake
// Realistic style, medium complexity

$fn=32;

// Main parameters for 3D printing
scale_factor = 2;  // Scale up from base 1 unit
base_size = 30 * scale_factor;  // ~60mm final size
crumb_size = 2.5 * scale_factor;  // Individual crumb size

// Colors
crumb_color = "#D4A574";  // Golden brown
darker_color = "#8B7355";  // Darker toasted parts
light_color = "#F5DEB3";  // Light flour parts

// Random crumb generator
module random_crumb(size) {
    // Create irregular crumb shape with boolean operations
    difference() {
        // Base rounded shape
        translate([rand(-1,1), rand(-1,1), rand(0,0.5)])
        scale([rand(0.8,1.2), rand(0.8,1.2), rand(0.7,1.3)])
        sphere(d=size);

        // Add irregularities
        for(i=[0:3]) {
            rotate([rand(0,360), rand(0,360), rand(0,360)])
            translate([rand(0,size/3), rand(0,size/3), rand(-size/4,size/4)])
            sphere(d=rand(size/3,size/2));
        }

        // Random holes for texture
        for(i=[0:2]) {
            translate([rand(-size/3,size/3), rand(-size/3,size/3), rand(-size/3,size/3)])
            sphere(d=rand(size/5,size/3));
        }
    }
}

// Generate random value
function rand(min,max) = min + (max-min)*(rands(0,1,1)[0]);

// Create a cluster of crumbs
module crumb_cluster(x, y, z, count) {
    translate([x, y, z]) {
        for(i=[0:count-1]) {
            // Position crumbs in natural pile
            translate([
                rand(-base_size/4, base_size/4),
                rand(-base_size/4, base_size/4),
                rand(0, base_size/8)
            ]) {
                color([rands(0.7,1,1)[0], rands(0.7,1,1)[0], rands(0.7,1,1)[0]]) {
                    // Vary colors slightly
                    if(rands(0,1,1)[0] > 0.7) {
                        color(darker_color) random_crumb(crumb_size * rand(0.8,1.2));
                    } else if(rands(0,1,1)[0] > 0.4) {
                        color(crumb_color) random_crumb(crumb_size * rand(0.8,1.2));
                    } else {
                        color(light_color) random_crumb(crumb_size * rand(0.8,1.2));
                    }
                }
            }
        }
    }
}

// Main streusel pile
module streusel_pile() {
    // Base layer - flatter crumbs
    for(i=[0:15]) {
        crumb_cluster(
            rand(-base_size/2, base_size/2),
            rand(-base_size/2, base_size/2),
            0,
            1
        );
    }

    // Middle layer - medium height
    for(i=[0:20]) {
        crumb_cluster(
            rand(-base_size/3, base_size/3),
            rand(-base_size/3, base_size/3),
            base_size/8,
            1
        );
    }

    // Top layer - taller clusters
    for(i=[0:10]) {
        crumb_cluster(
            rand(-base_size/4, base_size/4),
            rand(-base_size/4, base_size/4),
            base_size/4,
            rand(2,4)
        );
    }

    // Some individual crumbs scattered on top
    for(i=[0:8]) {
        translate([
            rand(-base_size/3, base_size/3),
            rand(-base_size/3, base_size/3),
            base_size/3 + rand(0,base_size/6)
        ]) {
            color(crumb_color) {
                scale([rand(0.8,1.2), rand(0.8,1.2), rand(0.8,1.2)])
                random_crumb(crumb_size * 0.7);
            }
        }
    }
}

// Create the streusel
streusel_pile();

