color_fat = [1, 0.95, 0.85];
color_rind = [0.1, 0.05, 0.05];

// Function to create wavy meat surface
module wavy_surface(height, freq, amp) {
    for(i = [0:freq-1]) {
        rotate([0, 0, i * 360/freq])
        translate([slice_diameter/4, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=slice_diameter/2, d=height*2, center=true);
    }
}

// Create fat marbling pattern
module fat_marbling() {
    color(color_fat)
    for(i = [0:15]) {
        translate([
            (rand(i*2)-0.5)*slice_diameter*0.8,
            (rand(i*3)-0.5)*slice_diameter*0.8,
            slice_thickness/2
        ])
        sphere(d=rand(i*5+2)+1);
    }
}

// Create ham slice base
module ham_slice() {
    difference() {
        // Main meat body
        color(color_meat)
        cylinder(h=slice_thickness, d=slice_diameter, center=true);

        // Create uneven surface
        intersection() {
            cylinder(h=slice_thickness*2, d=slice_diameter, center=true);
            wavy_surface(slice_thickness*0.3, 8, 1);
        }

        // Cut center hole for realistic slice appearance
        translate([0, 0, -slice_thickness])
        cylinder(h=slice_thickness*2, d=slice_diameter*0.15, center=false);
    }
}

