    color(color_rind)
    difference() {
        cylinder(h=slice_thickness*1.2, d=slice_diameter, center=true);
        cylinder(h=slice_thickness*1.3, d=slice_diameter*0.92, center=true);
    }
}

// Create texture dots for meat grain
module meat_grain() {
    color([color_meat[0]*0.8, color_meat[1]*0.8, color_meat[2]*0.8])
    for(i = [0:30]) {
        angle = rand(i*10) * 360;
        radius = rand(i*20) * slice_diameter/2 * 0.9;
