cream_color = "#FFF8DC"; // vanilla cream
sugar_color = "#FFFFFF"; // powdered sugar

// Helper module for creating a spiral
module spiral(height, radius, thickness, swirls) {
    for(i=[0:swirls*360]) {
        rotate([0, 0, i])
        translate([radius * cos(i/10), radius * sin(i/10), i/swirls/360*height])
        rotate([90, 0, 0])
        cylinder(h=thickness, r1=thickness/2, r2=thickness/3);
    }
}

// Horn cone pastry shell
module horn_cone() {
    color(cone_color) {
        difference() {
            // Outer cone
            cylinder(h=horn_length, r1=horn_radius, r2=horn_radius/3);

            // Inner hollow space
            translate([0, 0, cone_wall_thickness])
            cylinder(h=horn_length - cone_wall_thickness*2, r1=horn_radius-cone_wall_thickness, r2=horn_radius/3-cone_wall_thickness);

            // Opening at bottom
            translate([0, 0, -1])
            cylinder(h=cone_wall_thickness*2, r1=horn_radius/3, r2=horn_radius/3+2);
        }

        // Texture lines for pastry layers
        for(i=[0:3:horn_length]) {
            translate([0, 0, i])
            rotate([0, 0, i*2])
            scale([1.01, 1.01, 0.2])
            cylinder(h=0.5, r1=horn_radius*(1-i/horn_length*0.7), r2=horn_radius*(1-(i+0.5)/horn_length*0.7));
        }
    }
}

// Whipped cream filling
module cream_filling() {
    color(cream_color) {
        // Main cream swirl
        for(i=[0:20:horn_length-10]) {
            rotate([0, 0, i*5])
            translate([0, 0, i])
            scale([0.8, 0.8, 1])
            sphere(r=cream_thickness * (1 - i/horn_length*0.7));
        }

        // Swirl on top
        translate([0, 0, horn_length-5])
        rotate([0, 0, 45])
        scale([0.6, 0.6, 1.2])
        sphere(r=cream_thickness);

        // Additional cream peaks
        for(i=[0:120:360]) {
            rotate([0, 0, i])
            translate([horn_radius*0.4, 0, horn_length-3])
            sphere(r=cream_thickness/2);
        }
    }
}

// Sugar dusting
module sugar_dusting() {
    color(sugar_color, 0.3) {
        // Random sugar crystals
        for(i=[0:50]) {
            translate([
                horn_radius*0.8*cos(i*73),
                horn_radius*0.8*sin(i*73),
                horn_length*0.3 + i*0.5
            ])
            scale([0.2, 0.2, 0.1])
            sphere(r=0.5);
        }
    }
}

