// Calisson - Traditional French almond candy
// Overall dimensions: 50mm x 25mm x 12mm

$fn = 32;

// Main module for the calisson
module calisson() {
    // Diamond-shaped base (almond paste layer)
    color("beige") 
    difference() {
        // Main diamond body
        linear_extrude(height = 8) {
            polygon(points = [
                [0, 0], [25, 12.5], [0, 25], [-25, 12.5]
            ]);
        }

        // Rounded top edge
        translate([0, 12.5, 7]) 
        rotate([90, 0, 0])
        cylinder(h = 25, r1 = 12.5, r2 = 12.5, center = true);
    }

    // Royal icing layer on top
    color("ivory") 
    translate([0, 12.5, 7.5]) 
    rotate([90, 0, 0])
    cylinder(h = 25, r1 = 12.3, r2 = 12.3, center = true);

    // Candied melon pieces embedded in almond paste
    color("lightgreen") {
        for(i = [-1, 1]) {
            translate([i * 8, 12.5, 3]) 
            rotate([90, 0, 0])
            scale([1.2, 0.8, 1])
            cylinder(h = 10, r = 3);
        }
    }

    // Candied orange peel pieces
    color("orange") {
        for(i = [-0.7, 0.7]) {
            translate([i * 12, 12.5, 3]) 
            rotate([90, 0, 0])
            scale([1.5, 0.6, 1])
            cylinder(h = 8, r = 2.5);
        }
    }

    // Almond slivers on surface
    color("wheat") {
        for(a = [0:60:300]) {
            translate([cos(a) * 15, 12.5, 7.8]) 
            rotate([0, 0, a])
            scale([1, 0.3, 0.1])
            cube([6, 2, 0.5], center = true);
        }
    }

    // Texture lines on the sides
    color("beige") {
        for(z = [1:6]) {
            for(x = [-20:5:20]) {
                if(abs(x) < 22) {
                    translate([x, 12.5, z])
                    rotate([90, 0, 0])
                    scale([1, 0.05, 0.05])
                    cylinder(h = 25, r = 12.5);
                }
            }
        }
    }

    // Decorative edge pattern on icing
    color("ivory") {
        for(a = [0:15:360]) {
            translate([cos(a) * 12.2, sin(a) * 12.2 + 12.5, 8.2])
            sphere(r = 0.5);
        }
    }
}

// Create the calisson
