// Canele - French pastry with caramelized exterior and custard interior
// Overall dimensions: 60mm tall, 40mm diameter

// Colors
$caramel_dark = [101/255, 67/255, 33/255];
$caramel_light = [160/255, 82/255, 45/255];
$custard = [255/255, 248/255, 220/255];

// Main body parameters
body_height = 60;
top_diameter = 40;
bottom_diameter = 30;
wall_thickness = 3;

// Create the caramelized exterior shell
module canele_exterior() {
    color($caramel_dark) {
        difference() {
            // Main fluted body
            cylinder(h = body_height, 
                     r1 = bottom_diameter/2, 
                     r2 = top_diameter/2, 
                     $fn = 16);

            // Hollow interior
            translate([0, 0, wall_thickness])
                cylinder(h = body_height - wall_thickness*2, 
                         r1 = (bottom_diameter/2) - wall_thickness, 
                         r2 = (top_diameter/2) - wall_thickness, 
                         $fn = 16);

            // Create fluted edges by subtracting cylinders
            for (i = [0:15]) {
                rotate([0, 0, i * 22.5])
                    translate([top_diameter/2 - 2, 0, -1])
                        cylinder(h = body_height + 2, r = 2, $fn = 8);
            }
        }
    }
}

// Create the custard interior
module custard_interior() {
    color($custard) {
        translate([0, 0, wall_thickness + 1])
            cylinder(h = body_height - wall_thickness*2 - 2, 
                     r1 = (bottom_diameter/2) - wall_thickness - 1, 
                     r2 = (top_diameter/2) - wall_thickness - 1, 
                     $fn = 16);
    }
}

// Add caramelized texture bumps
module texture_bumps() {
    color($caramel_light) {
        // Small bumps on the surface
        for (i = [0:7]) {
            for (j = [0:15]) {
                rotate([0, 0, j * 22.5])
                    translate([
                        (bottom_diameter/2 + (top_diameter/2 - bottom_diameter/2) * (i/7)) * cos(j * 22.5),
                        (bottom_diameter/2 + (top_diameter/2 - bottom_diameter/2) * (i/7)) * sin(j * 22.5),
                        i * (body_height/7)
                    ])
                        sphere(r = 1.5, $fn = 8);
            }
        }
    }
}

// Create the slightly domed bottom
module domed_bottom() {
    color($caramel_dark) {
        translate([0, 0, -2])
            sphere(r = bottom_diameter/2, $fn = 16);
    }
}

// Create the characteristic canele shape
module canele() {
    union() {
        // Main components
        canele_exterior();
        custard_interior();

        // Add texture details
        texture_bumps();

        // Add domed bottom
        intersection() {
            domed_bottom();
            cube([bottom_diameter, bottom_diameter, 5], center = true);
        }
    }
}

// Generate the canele
canele();

// Gateau with multiple layers, frosting decoration and chocolate shavings
// Size: ~70mm diameter, 55mm height

