$vanilla = [0.98, 0.95, 0.88];
$strawberry = [0.95, 0.75, 0.80];

// Base cake layers module
module cake_layer(radius, height, color) {
    color(color)
    union() {
        difference() {
            cylinder(r=radius, h=height, center=false);
            // Create slightly domed top
            translate([0, 0, height - 2])
            scale([1, 1, 0.3])
            sphere(r=radius);
        }
        // Add slight rim for realistic edge
        translate([0, 0, 0.5])
        cylinder(r=radius+0.5, h=0.5, center=false);
    }
}

// Frosting swirl decoration
module frosting_swirl(x, y, z, size) {
    color($vanilla)
    translate([x, y, z])
    scale([size, size, size*1.5])
    sphere(r=2.5);
}

// Chocolate shavings
module chocolate_shaving(x, y, z, angle) {
    color($chocolate)
    translate([x, y, z])
    rotate([0, 0, angle])
    scale([1, 0.3, 0.1])
    cylinder(r=8, h=1);
}

// Top frosting decoration
module top_frosting() {
    union() {
        // Central swirl
        for(i = [0:30:330]) {
            rotate([0, 0, i])
            translate([0, 12, 52])
            scale([1, 1, 1.5])
            sphere(r=3);
        }

        // Decorative swirls around edge
        for(i = [0:45:315]) {
            rotate([0, 0, i])
            translate([20, 0, 52])
            frosting_swirl(0, 0, 0, 1);
        }

        // Additional smaller swirls
        for(i = [15:60:345]) {
            rotate([0, 0, i])
            translate([15, 0, 52])
            frosting_swirl(0, 0, 0, 0.7);
        }
    }
}

// Main gateau assembly
union() {
    // Bottom chocolate layer
    cake_layer(30, 15, $chocolate);

    // Middle strawberry layer
    translate([0, 0, 14.5])
    cake_layer(28, 12, $strawberry);

    // Top vanilla layer
    translate([0, 0, 26])
    cake_layer(26, 10, $vanilla);

    // Frosting layer between bottom and middle
    translate([0, 0, 14.2])
    color($vanilla)
    cylinder(r=29, h=0.6);

    // Frosting layer between middle and top
    translate([0, 0, 25.7])
    color($vanilla)
    cylinder(r=27, h=0.6);

    // Side frosting coating
    color($vanilla)
    cylinder(r1=31, r2=27, h=28, center=false);

    // Top frosting decoration
    top_frosting();

    // Chocolate shavings on top
    for(i = [0:20:340]) {
        rotate([0, 0, i])
        translate([18, 0, 54])
        chocolate_shaving(0, 0, 0, i*2);
    }

    // Additional shavings at center
    for(i = [10:30:370]) {
        rotate([0, 0, i])
        translate([8, 0, 54])
        chocolate_shaving(0, 0, 0, -i*1.5);
    }
}

