// OpenSCAD code for a realistic millasson (traditional cornmeal cake)
// Dimensions approximately 80mm diameter, 25mm height

// Main colors
color_beige = [0.95,0.88,0.70];
color_dark_beige = [0.85,0.78,0.60];
color_brown = [0.65,0.45,0.30];

// Parameters
diameter = 80;
height = 25;
edge_radius = 2;
slice_lines = 8;
corn_lines = 12;

module millasson_base() {
    // Main cake body - slightly domed top
    union() {
        // Base cylinder with rounded edges
        difference() {
            cylinder(h=height, d=diameter, $fn=100);
            translate([0,0,-0.5])
                cylinder(h=height+1, d=diameter-4, $fn=100);
        }

        // Domed top
        translate([0,0,height-2])
            scale([1,1,0.3])
                sphere(d=diameter-4, $fn=100);
    }
}

// Surface texture - cornmeal grains
module cornmeal_texture() {
    difference() {
        millasson_base();

        // Random small holes for cornmeal texture
        for(i = [0:corn_lines-1]) {
            angle = i * 360/corn_lines;
            for(j = [0:3]) {
                radius = (diameter/2 - 5) * (0.2 + j*0.2);
                x = cos(angle) * radius;
                y = sin(angle) * radius;

                translate([x,y,height/2 + 2 + 3*sin(i+j)])
                    sphere(d=1.2, $fn=8);
            }
        }
    }
}

// Baking marks - slightly darker surface areas
module baking_marks() {
    intersection() {
        millasson_base();

        for(i = [0:slice_lines-1]) {
            angle = i * 360/slice_lines;

            // Radial darker areas
            rotate([0,0,angle])
                translate([diameter/4,0,0])
                    scale([diameter/2,5,height])
                        cube([1,1,1], center=true);
        }
    }
}

// Sliced wedge (optional - shows interior)
module sliced_wedge() {
    difference() {
        union() {
            color(color_beige) cornmeal_texture();
            color(color_dark_beige) baking_marks();
        }

        // Cut out a 40-degree wedge
        rotate([0,0,-20])
            translate([0,-diameter/2-5,0])
                cube([diameter+10, diameter+10, height+10]);
    }
}

// Assemble the complete millasson
union() {
    // Main body with texture
    color(color_beige) cornmeal_texture();

    // Baking marks
    color(color_dark_beige) baking_marks();

    // Bottom edge detail
    color(color_brown)
        difference() {
            cylinder(h=2, d=diameter, $fn=100);
            cylinder(h=3, d=diameter-4, $fn=100);
        }
}

