// Create the main twisted ribbon body with slight wave
module twisted_ribbon() {
    color("Goldenrod")
    difference() {
        // Main twisted strip
        union() {
            for (i = [0:5:length-10]) {
                translate([i, 0, 0])
                rotate([0, 0, i*2])
                scale([1, 0.9 + 0.1*sin(i*15), 1])
                hull() {
                    translate([0, -width/2, 0])
                    cylinder(h=thickness, d=thickness*2.5, $fn=12);
                    translate([0, width/2, 0])
                    cylinder(h=thickness, d=thickness*2.5, $fn=12);
                }
            }
        }

        // Center hole for classic bugnes shape
        translate([length/2, 0, -1])
        rotate([0, 0, 30])
        scale([1, 0.7, 1])
        cylinder(h=thickness+2, d=15, $fn=32);
    }
}

// Sugar coating with random crystals
module sugar_coating() {
    color("AntiqueWhite")
    union() {
        // Thin sugar layer on top
        translate([0, 0, thickness/2 - 0.1])
        scale([0.98, 0.98, 0.1])
        linear_extrude(height=0.2)
        projection()
        twisted_ribbon();

        // Icing sugar crystals
        for (i = [0:30]) {
            translate([
                rands(-width/2, width/2, 1)[0],
                rands(-length/2, length/2, 1)[0],
                thickness/2 + rands(0, 0.5, 1)[0]
            ])
            sphere(d=rands(0.3, 0.8, 1)[0], $fn=6);
        }
    }
}

// Surface texture - small bubbles from frying
