// Create the boat shape
module navette_body() {
    // Main hull shape using spheres
    difference() {
        // Outer shell - two intersecting spheres to form boat shape
        intersection() {
            // First sphere - front half
            translate([length/4, 0, 0])
                scale([1.5, 0.8, 0.6])
                sphere(d=width);

            // Second sphere - back half
            translate([-length/4, 0, 0])
                scale([1.5, 0.8, 0.6])
                sphere(d=width);

            // Cut to make flat bottom
            translate([0, 0, -height/2])
                cube([length*2, width*2, height], center=true);
        }

        // Hollow out the interior
        translate([0, 0, crust_thickness])
            scale([0.8, 0.7, 0.7])
            intersection() {
                translate([length/4, 0, 0])
                    scale([1.5, 0.8, 0.6])
                    sphere(d=width);
                translate([-length/4, 0, 0])
                    scale([1.5, 0.8, 0.6])
                    sphere(d=width);
                translate([0, 0, -height/2])
                    cube([length*2, width*2, height], center=true);
            }
    }
}

// Add decorative ridges along the top edge
module crust_edge() {
    // Top rim
    translate([0, 0, height - crust_thickness/2])
        scale([0.95, 0.9, 0.3])
        difference() {
            intersection() {
                translate([length/4, 0, 0])
                    scale([1.5, 0.8, 0.6])
                    sphere(d=width);
                translate([-length/4, 0, 0])
                    scale([1.5, 0.8, 0.6])
                    sphere(d=width);
                translate([0, 0, -height/2])
                    cube([length*2, width*2, height], center=true);
            }
            translate([0, 0, -1])
                scale([0.9, 0.85, 0.5])
                intersection() {
                    translate([length/4, 0, 0])
                        scale([1.5, 0.8, 0.6])
                        sphere(d=width);
                    translate([-length/4, 0, 0])
                        scale([1.5, 0.8, 0.6])
                        sphere(d=width);
                    translate([0, 0, -height/2])
                        cube([length*2, width*2, height], center=true);
                }
        }
}

// Create sesame seeds
module sesame_seeds() {
    seed_size = 1.2;
    spacing = 3;

    // Top surface seeds
    for (x = [-length/2 + 5 : spacing : length/2 - 5]) {
        for (y = [-width/2 + 3 : spacing : width/2 - 3]) {
            // Calculate height on curved surface
            z_pos = height * 0.8 * (1 - (abs(x) / (length/2)) * 0.3) - seed_size;

            // Only place seeds on top curved surface
            if (abs(y) < width/3) {
                translate([x, y, z_pos])
                    rotate([0, 0, rand(x*y)*360])
                    scale([1, 0.7, 0.4])
                    sphere(d=seed_size);
            }
        }
    }
}

// Add subtle texture to crust
module crust_texture() {
    detail_size = 0.3;
    step = 2;

    // Small indentations for realistic texture
    for (x = [-length/2 + 2 : step : length/2 - 2]) {
        for (y = [-width/2 + 1 : step : width/2 - 1]) {
            z_pos = height * 0.3 * (1 - abs(x)/(length/2)) + 2;
            translate([x, y, z_pos])
                sphere(d=detail_size);
        }
    }
}

// Main assembly
