// Religieuse - Classic French choux pastry dessert
// Two stacked choux buns filled with pastry cream and iced with chocolate ganache

$fn = 36;  // Smooth curves for food realism

// Main dimensions (mm)
bottom_diameter = 40;
top_diameter = 25;
bottom_height = 25;
top_height = 18;
filling_thickness = 3;
icing_thickness = 2;
wall_thickness = 2.2;

// Colors
choux_color = "#F5DEB3";  // Golden pastry
icing_color = "#4A2C17";  // Dark chocolate
filling_color = "#FFF8DC";  // Vanilla cream

// Module for choux pastry texture (simple bumps)
module choux_texture(height, diameter) {
    difference() {
        sphere(d = diameter);
        for (i = [0:15]) {
            rotate([0, 0, i * 24]) 
            translate([diameter/2 - 2, 0, height/2 - 2])
            sphere(d = 2.5);
        }
    }
}

// Bottom choux bun
module bottom_choux() {
    color(choux_color) {
        difference() {
            // Main bun shape - slightly flattened sphere
            scale([1, 1, 0.8])
            choux_texture(bottom_height, bottom_diameter);

            // Hollow interior for filling
            translate([0, 0, -wall_thickness])
            scale([1, 1, 0.7])
            sphere(d = bottom_diameter - 2*wall_thickness);

            // Bottom flat surface for printing
            translate([0, 0, -bottom_height/2])
            cube([bottom_diameter + 2, bottom_diameter + 2, bottom_height], center = true);
        }

        // Slight ridge pattern
        for (i = [1:3]) {
            rotate([90, 0, 0])
            translate([0, 0, -bottom_height/2 + i*4])
            cylinder(h = 0.5, d = bottom_diameter - i*4, center = true);
        }
    }
}

// Top choux bun
module top_choux() {
    color(choux_color) {
        difference() {
            // Smaller sphere for top
            scale([1, 1, 0.9])
            choux_texture(top_height, top_diameter);

            // Hollow interior
            translate([0, 0, -wall_thickness])
            sphere(d = top_diameter - 2*wall_thickness);

            // Flat bottom for stacking
            translate([0, 0, -top_height/2])
            cube([top_diameter + 2, top_diameter + 2, top_height], center = true);
        }

        // Ridge pattern
        for (i = [1:2]) {
            rotate([90, 0, 0])
            translate([0, 0, -top_height/2 + i*3])
            cylinder(h = 0.5, d = top_diameter - i*3, center = true);
        }
    }
}

// Filling layer between buns
module filling() {
    color(filling_color) {
        translate([0, 0, bottom_height/2 - 1])
        cylinder(h = filling_thickness, d = bottom_diameter - 4, $fn = 48);
    }
}

// Chocolate icing drip effect
module chocolate_icing() {
    color(icing_color) {
        // Main icing on top bun
        translate([0, 0, bottom_height + top_height/2 + icing_thickness/2])
        scale([1, 1, 0.6])
        sphere(d = top_diameter + 2);

        // Drip details
        for (i = [0:5]) {
            angle = i * 60;
            rotate([0, 0, angle])
            translate([top_diameter/2 + 1, 0, bottom_height + top_height/2 - 2])
            scale([0.3, 0.3, 1])
            cylinder(h = 4 + (i%3)*2, d = 2, $fn = 8);
        }

        // Bottom bun icing band
        translate([0, 0, bottom_height/2 + 2])
        cylinder(h = 3, d = bottom_diameter + 1, $fn = 48);
    }
}

// Assembly
union() {
    // Bottom bun
    bottom_choux();

    // Filling layer
    filling();

    // Top bun
    translate([0, 0, bottom_height - 3])
    top_choux();

    // Chocolate icing
    chocolate_icing();
}

