    translate([0, 0, bottom_height + top_height/2 + 3])
    rotate([90, 0, 0])
    linear_extrude(height = 1, twist = 360)
    circle(d = 8);
}

// Realistic 3D printable puit (Dutch syrup waffle / stroopwafel)
// Medium complexity model with proper wall thickness

// Main parameters
waffle_diameter = 70;
waffle_thickness = 8;
core_thickness = 3;
waffle_color = [0.8, 0.6, 0.4];
caramel_color = [0.6, 0.4, 0.2];

// Create the complete puit
module puit() {
    // Bottom waffle layer
    difference() {
        waffle_layer();

        // Central hole for caramel core
        translate([0, 0, waffle_thickness - core_thickness])
            cylinder(h=waffle_thickness + 1, d=30, $fn=32);
    }

    // Caramel core
    translate([0, 0, waffle_thickness - core_thickness])
        color(caramel_color)
        cylinder(h=core_thickness, d=30, $fn=32);

    // Top waffle layer
    translate([0, 0, waffle_thickness])
        waffle_layer();
}

// Create a single waffle layer with honeycomb pattern
module waffle_layer() {
    color(waffle_color)
    difference() {
        // Main disk
        cylinder(h=waffle_thickness, d=waffle_diameter, $fn=64);

        // Honeycomb pattern
        honeycomb_pattern();
    }
}

// Create honeycomb pattern on waffle
module honeycomb_pattern() {
    // Parameters for honeycomb
    hex_radius = 3.5;
    hex_height = waffle_thickness - 1;
    rows = 5;

    // Generate hexagonal pattern
    for (row = [0:rows]) {
        for (col = [0:rows]) {
            // Calculate positions for hexagonal grid
            x_offset = (col * 2 * hex_radius * cos(30)) - (row % 2) * hex_radius * cos(30);
            y_offset = (row * 1.5 * hex_radius);

            // Only create hexagons within the circle
            if (norm([x_offset, y_offset]) < (waffle_diameter/2 - hex_radius)) {
                translate([x_offset, y_offset, 0])
                    cylinder(h=hex_height, r=hex_radius, $fn=6);

                // Mirror for other side
                translate([-x_offset, y_offset, 0])
                    cylinder(h=hex_height, r=hex_radius, $fn=6);

                // Mirror for bottom
                translate([x_offset, -y_offset, 0])
                    cylinder(h=hex_height, r=hex_radius, $fn=6);

                translate([-x_offset, -y_offset, 0])
                    cylinder(h=hex_height, r=hex_radius, $fn=6);
            }
        }
    }

    // Edge pattern squares
    edge_squares();
}

// Create edge pattern squares
module edge_squares() {
    square_size = 4;
    square_height = waffle_thickness - 1;
    edge_radius = waffle_diameter/2 - square_size/2;

    for (angle = [0:30:330]) {
        rotate([0, 0, angle])
            translate([edge_radius, 0, 0])
                cube([square_size, square_size, square_height], center=true);
    }
}

// Generate the complete puit
puit();

