// Realistic kiwi fruit slice - medium complexity
// Kiwi slice with textured skin, flesh, seeds and core detail

// Overall dimensions
kiwi_diameter = 60;
kiwi_thickness = 10;
wall_thickness = 1.5;

// Colors
skin_color = "#5D4037";
flesh_color = "#8BC34A";
seed_color = "#212121";
core_color = "#F5F5DC";

// Module for individual seeds
module seed() {
    color(seed_color)
    translate([0, 0, -0.2])
    scale([1, 1, 0.3])
    sphere(0.8, $fn=8);
}

// Module for seed ring pattern
module seed_ring(radius, count) {
    for(i = [0:count-1]) {
        angle = i * 360 / count;
        translate([radius * cos(angle), radius * sin(angle), kiwi_thickness/2 - 0.5])
        seed();
    }
}

// Module for fuzzy texture on skin
module skin_texture() {
    difference() {
        // Main skin cylinder
        color(skin_color)
        cylinder(h=kiwi_thickness, r=kiwi_diameter/2, $fn=64);

        // Cut to half-cylinder
        translate([-kiwi_diameter/2, -kiwi_diameter/2, -0.1])
        cube([kiwi_diameter, kiwi_diameter/2, kiwi_thickness+0.2]);
    }

    // Add small bumps for texture
    for(i = [0:20]) {
        angle = i * 18;
        bump_radius = kiwi_diameter/2 - 0.5;
        translate([bump_radius * cos(angle), bump_radius * sin(angle), kiwi_thickness/2])
        scale([1, 1, 0.2])
        sphere(0.5, $fn=6);
    }
}

// Module for flesh with core
module flesh() {
    difference() {
        // Main flesh
        color(flesh_color)
        translate([0, 0, wall_thickness])
        cylinder(h=kiwi_thickness - 2*wall_thickness, r=kiwi_diameter/2 - wall_thickness, $fn=64);

        // Cut to half-cylinder
        translate([-kiwi_diameter/2, -kiwi_diameter/2, wall_thickness-0.1])
        cube([kiwi_diameter, kiwi_diameter/2, kiwi_thickness]);

        // Core cavity
        color(core_color)
        translate([0, 0, wall_thickness - 0.1])
        cylinder(h=kiwi_thickness - 2*wall_thickness + 0.2, r=kiwi_diameter/8, $fn=32);
    }

    // Add actual core
    color(core_color)
    translate([0, 0, wall_thickness])
    cylinder(h=kiwi_thickness - 2*wall_thickness, r=kiwi_diameter/10, $fn=32);
}

// Module for growth rings in flesh
module growth_rings() {
    for(r = [kiwi_diameter/6:kiwi_diameter/24:kiwi_diameter/2 - wall_thickness - 2]) {
        color(flesh_color)
        translate([0, 0, kiwi_thickness/2])
        linear_extrude(height=0.5)
        offset(delta=-0.5)
        circle(r=r, $fn=64);
    }
}

// Main assembly
union() {
    // Bottom skin
    skin_texture();

    // Flesh layer
    flesh();

    // Growth rings
    growth_rings();

    // Seed patterns
    seed_ring(kiwi_diameter/3, 12);
    seed_ring(kiwi_diameter/2.5, 16);
    seed_ring(kiwi_diameter/2.2, 20);
}

// ÃŽle flottante - a classic French dessert
// Floating island of meringue on crÃ¨me anglaise with caramel threads

// Main dimensions
island_diameter = 45;
island_height = 35;
custard_bowl_diameter = 80;
custard_bowl_height = 12;
caramel_length = 25;

// Colors
meringue_color = [1, 0.98, 0.94];
custard_color = [1, 0.96, 0.82];
caramel_color = [0.72, 0.45, 0.2];

// Create the complete dessert
module flottante() {
    // CrÃ¨me anglaise base
    color(custard_color) translate([0, 0, 0]) custard_base();

    // Meringue island
    color(meringue_color) translate([0, 0, custard_bowl_height - 5]) meringue_island();

    // Caramel threads
    color(caramel_color) caramel_decoration();
}

// CrÃ¨me anglaise base - shallow bowl shape
module custard_base() {
    difference() {
        // Outer bowl
        cylinder(h = custard_bowl_height, d = custard_bowl_diameter, $fn = 64);

        // Inner hollow for liquid
        translate([0, 0, 2]) 
            cylinder(h = custard_bowl_height, d = custard_bowl_diameter - 6, $fn = 64);
    }

    // Slight curve at bottom for realistic bowl shape
    translate([0, 0, -1])
        sphere(d = custard_bowl_diameter, $fn = 64);
}

// Meringue island with fluffy texture
module meringue_island() {
    union() {
        // Base meringue shape - slightly flattened sphere
        scale([1, 1, 0.8])
            sphere(d = island_diameter, $fn = 48);

        // Add fluffy peaks on top
        for(i = [0:15]) {
            rotate([0, 0, i * 24])
                translate([island_diameter/3, 0, island_height/2 - 5])
                    scale([0.5 + sin(i*5)*0.2, 0.5 + cos(i*3)*0.2, 1 + sin(i*7)*0.3])
                        sphere(d = 8 + sin(i*2)*3, $fn = 16);
        }

        // Add smaller texture bumps
        for(i = [0:20]) {
            angle = i * 18;
            radius = island_diameter/2 * (0.7 + sin(i*3)*0.2);
            translate([cos(angle)*radius, sin(angle)*radius, island_height/2 - 8 + sin(i*5)*3])
                sphere(d = 2 + sin(i*4)*1, $fn = 8);
        }
    }
}

// Caramel decoration threads
module caramel_decoration() {
    // Create crossing caramel threads
    for(i = [0:5]) {
        angle = i * 30;
        height = custard_bowl_height + 15 + sin(i*2)*5;

        // Main thread
        translate([cos(angle)*island_diameter/2, sin(angle)*island_diameter/2, height])
            rotate([90 - sin(angle)*10, 0, angle])
                cylinder(h = caramel_length, d1 = 1.2, d2 = 0.4, $fn = 8);

        // Crossing thread
        translate([cos(angle+15)*island_diameter/3, sin(angle+15)*island_diameter/3, height + 3])
            rotate([85 + cos(angle)*8, 0, angle + 45])
                cylinder(h = caramel_length*0.8, d1 = 1, d2 = 0.3, $fn = 8);
    }

    // Caramel droplets
    for(i = [0:8]) {
        angle = i * 45;
        radius = island_diameter/2 * 0.8;
        translate([cos(angle)*radius, sin(angle)*radius, custard_bowl_height + 20])
            sphere(d = 1.5 + sin(i*3)*0.5, $fn = 8);
    }
}

// Render the complete dessert
