dragonfruit_size = 25;      // mm
seed_count = 12;

// Main module
module dragonfruit_tartlet() {
    color("wheat")
    difference() {
        // Tartlet crust
        union() {
            // Base crust
            cylinder(h=crust_thickness, d=tartlet_diameter);

            // Side crust with scalloped edge
            for(i = [0:11]) {
                rotate([0, 0, i*30])
                translate([tartlet_diameter/2 - crust_thickness/2, 0, crust_thickness/2])
                cylinder(h=tartlet_height-crust_thickness, d=crust_thickness);
            }
        }

        // Hollow center for filling
        translate([0, 0, crust_thickness])
        cylinder(h=tartlet_height-crust_thickness, d=tartlet_diameter-crust_thickness*2);
    }

    // Filling
    color("hotpink")
    translate([0, 0, crust_thickness])
    cylinder(h=filling_height, d=tartlet_diameter-crust_thickness*2-2);

    // Dragonfruit half on top
    color("crimson")
    translate([0, 0, crust_thickness+filling_height])
    difference() {
        // Main fruit body
        sphere(d=dragonfruit_size);

        // Cut flat bottom
        translate([0, 0, -dragonfruit_size/2-1])
        cube([dragonfruit_size*2, dragonfruit_size*2, dragonfruit_size], center=true);

        // Scoop out inside
        translate([0, 0, -5])
        sphere(d=dragonfruit_size-6);
    }

    // Dragonfruit peel
    color("darkgreen")
    difference() {
        sphere(d=dragonfruit_size+2);
        sphere(d=dragonfruit_size);
    }

    // Dragonfruit seeds
    color("black")
    for(i = [0:seed_count-1]) {
        angle = i * (360/seed_count);
        radius = dragonfruit_size/3;

        // Spiral pattern
        rotate([0, 0, angle + i*15])
        translate([radius*sin(i*30), radius*cos(i*30), 0])
