// Realistic lemonade in a glass with ice cubes and lemon slice
// Dimensions: ~80mm tall glass with 50mm diameter

$fn = 32;

// Main colors
glass_color = [0.9, 0.95, 1, 0.3];
lemonade_color = [1, 0.9, 0.3, 0.7];
ice_color = [0.95, 0.98, 1, 0.6];
lemon_color = [1, 0.8, 0];
rind_color = [0.9, 0.7, 0];

// Glass parameters
glass_height = 80;
glass_diameter = 50;
glass_thickness = 3;
base_thickness = 5;

// Lemonade parameters
lemonade_height = 60;

// Ice cube parameters
ice_size = 8;
ice_count = 5;

// Lemon slice parameters
lemon_diameter = 30;
lemon_thickness = 4;
slice_thickness = 2;

// Glass with lemonade
module glass_with_lemonade() {
    difference() {
        // Outer glass
        color(glass_color)
        cylinder(h=glass_height, d=glass_diameter, $fn=64);

        // Hollow inside
        translate([0, 0, base_thickness])
        cylinder(h=glass_height-base_thickness+1, d=glass_diameter-glass_thickness*2, $fn=64);
    }

    // Base
    translate([0, 0, 0])
    color(glass_color)
    cylinder(h=base_thickness, d=glass_diameter+2, $fn=64);

    // Lemonade inside
    translate([0, 0, base_thickness])
    color(lemonade_color)
    cylinder(h=lemonade_height-base_thickness, d=glass_diameter-glass_thickness*2-1, $fn=64);
}

// Ice cube with rounded edges
module ice_cube(size) {
    color(ice_color) {
        // Main cube
        cube([size*0.9, size*0.9, size*0.9], center=true);

        // Rounded corners using spheres
        for(x=[-1, 1])
        for(y=[-1, 1])
        for(z=[-1, 1]) {
            translate([x*size*0.45, y*size*0.45, z*size*0.45])
            sphere(r=size*0.35);
        }
    }
}

// Generate random ice cubes
module floating_ice() {
    for(i=[0:ice_count-1]) {
        angle = i * 137.5; // Golden angle for natural distribution
        radius = 15 + sin(i*2) * 10;

        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            lemonade_height - 10 - i*3
        ]) {
            rotate([rand(i*10), rand(i*20), rand(i*30)])
            ice_cube(ice_size + rand(i*5)*2);
        }
    }
}

// Lemon slice with segments and texture
module lemon_slice() {
    color(lemon_color) {
        // Main slice
        cylinder(h=slice_thickness, d=lemon_diameter);

        // Rind
        color(rind_color)
        difference() {
            cylinder(h=slice_thickness, d=lemon_diameter+1);
            cylinder(h=slice_thickness+1, d=lemon_diameter-2);
        }

        // Segments
        for(i=[0:5]) {
            rotate([0, 0, i*60])
            translate([0, 0, -0.1])
            linear_extrude(height=slice_thickness+0.2)
            polygon(points=[
                [0, 0],
                [lemon_diameter/2-4, -2],
                [lemon_diameter/2-4, 2]
            ]);
        }

        // Center
        translate([0, 0, -0.1])
        cylinder(h=slice_thickness+0.2, d=4);
    }
}

// Helper for random values
function rand(seed) = (seed * 9301 + 49297) % 233280 / 233280;

// Main assembly
glass_with_lemonade();
floating_ice();

// Lemon slice on rim
translate([glass_diameter/2-5, 0, glass_height-10])
rotate([0, 90, 0])
lemon_slice();

