can_radius = 32;
wall_thickness = 1.5;

// Can body module
module can_body() {
    difference() {
        // Outer cylinder
        cylinder(h=can_height, r=can_radius, center=false);

        // Inner hollow space
        translate([0, 0, wall_thickness])
            cylinder(h=can_height-2*wall_thickness, r=can_radius-wall_thickness, center=false);

        // Top opening
        translate([0, 0, can_height-wall_thickness])
            cylinder(h=wall_thickness+1, r=can_radius-10, center=false);
    }
}

// Curved top module
module can_top() {
    // Top dome
    translate([0, 0, can_height-wall_thickness])
        scale([1, 1, 0.3])
            sphere(r=can_radius);

    // Opening lip
    translate([0, 0, can_height-wall_thickness+2])
        cylinder(h=3, r1=can_radius-10, r2=can_radius-8, center=false);
}

// Label texture pattern
module label_texture() {
    for(i=[0:5:can_height-20]) {
        // Horizontal ridges
        translate([0, 0, i+15])
            difference() {
                cylinder(h=0.5, r=can_radius+0.2, center=false);
                cylinder(h=0.5, r=can_radius, center=false);
            }
    }
}

// Cola logo area
