// Bottom ridge pattern
module bottom_ridges() {
    for(i=[0:30:359]) {
        rotate([0, 0, i]) {
            translate([can_radius-3, 0, 0])
                cube([6, 1, 5], center=true);
        }
    }
}

// Main assembly
union() {
    color(can_color) {
        // Main can body
        can_body();

        // Curved top
        can_top();

        // Bottom base
        translate([0, 0, -0.5])
            cylinder(h=5, r=can_radius, center=false);
    }

    // Label area
