// Realistic Energy Tonic Bottle - Medium Complexity
// Features textured cap, label, embossed details, and liquid level

$fn = 36;

// Main dimensions
bottle_height = 85;
bottle_diameter = 35;
cap_height = 15;
cap_diameter = 38;
label_height = 30;
liquid_level = 65;

// Colors
color_bottle = [0.1, 0.7, 0.9, 0.6];  // Translucent blue
color_cap = [0.2, 0.2, 0.2];         // Dark gray
color_label = [0.9, 0.9, 0.9];       // White
color_text = [0.1, 0.1, 0.9];        // Blue
color_liquid = [0.0, 0.8, 0.4, 0.8]; // Green tonic

module textured_cap() {
    difference() {
        // Main cap cylinder
        cylinder(h=cap_height, d=cap_diameter);

        // Grip ridges on sides
        for(i = [0:30:359]) {
            rotate([0, 0, i])
                translate([cap_diameter/2 - 2, 0, 0])
                    cube([2, 2, cap_height], center=true);
        }

        // Top indentation
        translate([0, 0, cap_height - 2])
            cylinder(h=3, d=cap_diameter - 10);
    }
}

module bottle_body() {
    difference() {
        // Main bottle shape
        union() {
            cylinder(h=bottle_height, d=bottle_diameter);

            // Neck
            translate([0, 0, bottle_height])
                cylinder(h=10, d1=bottle_diameter, d2=cap_diameter - 2);
        }

        // Hollow interior
        translate([0, 0, -1])
            cylinder(h=bottle_height + 12, d=bottle_diameter - 4);
    }
}

module embossed_text(text) {
    // Simple embossed lettering
    for(i = [0:len(text)-1]) {
        translate([i*4 - len(text)*2, 0, 0.5])
            cube([3, 0.5, 1]);
    }
}

module label() {
    difference() {
        // Label background
        color(color_label)
        translate([0, 0, bottle_height/2 - 5])
            cylinder(h=label_height, d=bottle_diameter + 0.5);

        // Label edges
        translate([0, 0, bottle_height/2 - 5])
            cylinder(h=label_height + 2, d=bottle_diameter + 0.2);

        // Text area
        translate([0, 0, bottle_height/2 - 4])
            cylinder(h=label_height - 2, d=bottle_diameter - 2);
    }

    // Embossed tonic symbol
    color(color_text)
    translate([0, -bottle_diameter/2 - 0.3, bottle_height/2 + 2])
        rotate([90, 0, 0])
            embossed_text("ENERGY+");
}

module liquid_fill() {
    color(color_liquid)
    translate([0, 0, 1])
        cylinder(h=liquid_level - 2, d=bottle_diameter - 4);
}

module bubbles() {
    // Carbonation bubbles
    color([1, 1, 1, 0.4])
    for(i = [0:5:liquid_level]) {
        translate([
            sin(i*5)*bottle_diameter/4,
            cos(i*3)*bottle_diameter/4,
            i
        ])
            sphere(d=1 + (i%3));
    }
}

// Assemble the tonic bottle
union() {
    // Bottle body
    color(color_bottle)
    bottle_body();

    // Liquid inside
    liquid_fill();

    // Bubbles for effect
    bubbles();

    // Label on bottle
    label();

    // Cap on top
    color(color_cap)
    translate([0, 0, bottle_height + 10])
        textured_cap();
}

// Realistic seltzer can with pull-tab, condensation beads and base rim
// Overall height â‰ˆ 75 mm, diameter â‰ˆ 45 mm, wall thickness 1 mm

