rim_color  = [0.75,0.82,0.88]; // slightly darker rim

// Main can body
module can_body(h=70, d=45, wall=1){
    difference(){
        cylinder(h=h, d=d);
        translate([0,0,wall]) cylinder(h=h, d=d-2*wall);
    }
}

// Top dome for realistic can shape
