    color(tab_color)
    translate([0, d/2-3, 71.5])
    union(){
        // tab ring
        rotate([90,0,0])
        torus(2, 0.6, $fn=20);
        // tab plate
        translate([-4,0,0])
        cube([8,1.5,0.4]);
    }
}

// Helper for torus (needed for ring)
module torus(R,r){
    rotate_extrude()
    translate([R,0,0]) circle(r);
}

