// Spritzer Cookie - Realistic Style, Medium Complexity

// Colors
cookie_color = "#D4A574";
chocolate_color = "#4A2C2A";
sugar_color = "#FFFFFF";

// Main module
module spritzer_cookie() {
    // Base cookie shape - ridged cylinder
    difference() {
        union() {
            // Main cookie body with ridges
            for (i = [0:15:360]) {
                rotate([0, 0, i])
                translate([0, 0, 0])
                cylinder(h=8, r1=22, r2=20, $fn=6);
            }

            // Smooth top surface
            translate([0, 0, 8])
            cylinder(h=2, r=20, $fn=64);
        }

        // Center hole for classic spritzer shape
        translate([0, 0, -1])
        cylinder(h=12, r=4, $fn=32);
    }

    // Chocolate coating on bottom half
    color(chocolate_color)
    translate([0, 0, -0.5])
    difference() {
        cylinder(h=5.5, r1=20.5, r2=21, $fn=64);

        // Remove center from chocolate coating
        translate([0, 0, -1])
        cylinder(h=7, r=4, $fn=32);

        // Create wavy bottom edge
        for (i = [0:10:350]) {
            rotate([0, 0, i])
            translate([19, 0, 0])
            rotate([90, 0, 0])
            cylinder(h=2, r1=1.5, r2=0.5, $fn=8);
        }
    }

    // Sugar crystals on top
    color(sugar_color)
    for (i = [1:35]) {
        // Random positioning for sugar
        angle = i * 137.5;
        radius = 8 + (i % 12);
        x = cos(angle) * radius;
