        translate([30*sin(i*15), 30*cos(i*15), 9.5])
        scale([1, 0.3, 0.3])
        sphere(r=1.5);
    }
}

// Sesame seeds (til)
module sesame_seeds() {
    color("darkgoldenrod")
    for(i=[0:40]) {
        a = i*137.5;
        r = pow(i, 0.5) * 2.8;
        translate([r*cos(a), r*sin(a), 10.2])
        rotate([90, 0, 0])
        scale([0.8, 0.8, 0.3])
        cylinder(h=1, r=0.8);
    }
}

// Jaggery coating drips
module jaggery_drips() {
    color("saddlebrown")
    union() {
        // Main jaggery layer
        translate([0, 0, 10])
        cylinder(h=1.5, r=32, center=false);

        // Drips around edge
        for(i=[0:11]) {
            rotate([0, 0, i*30])
            translate([32, 0, 10])
            rotate([90, 0, 0])
            scale([1, 1, 3])
            cylinder(h=3, r1=2, r2=0.5);
        }
    }
}

// Coconut flakes
module coconut_flakes() {
    color("white")
    for(i=[0:25]) {
        a = i*14.4 + rand(i)*10;
        r = 15 + rand(i)*15;
        translate([r*cos(a), r*sin(a), 11.8])
        rotate([0, 0, a])
        scale([1, 0.5, 0.1])
        cube([3, 2, 0.3], center=true);
    }
}

// Helper for randomness
function rand(n) = (n*13)%7/10;

// Assembly
union() {
    pitha_base();
    pitha_texture();
    jaggery_drips();
    sesame_seeds();
    coconut_flakes();
}

// Sencha Green Tea Leaves in a Traditional Bowl
// Realistic style, medium complexity

bowl_wall = 1.5;
bowl_radius = 25;
bowl_height = 15;
leaf_thickness = 0.8;

module bowl() {
    difference() {
        // Outer bowl shape
        cylinder(h = bowl_height, r1 = bowl_radius, r2 = bowl_radius * 0.9, $fn = 64);

        // Hollow interior
        translate([0, 0, bowl_wall])
            cylinder(h = bowl_height - bowl_wall, r1 = bowl_radius - bowl_wall, r2 = (bowl_radius * 0.9) - bowl_wall, $fn = 64);
    }
}

module tea_leaf(length, width, curl) {
    color([0.2, 0.5, 0.1])
    difference() {
        // Main leaf body
        scale([width, length, leaf_thickness])
            sphere(r = 1, $fn = 16);

        // Serrated edges using small cylinders
        for(i = [0:5:length * 10]) {
            rotate([0, 0, i])
                translate([width + 0.3, 0, leaf_thickness/2])
                    cylinder(h = leaf_thickness * 1.2, r = 0.2, $fn = 8);
        }

        // Central vein
        translate([0, -length/2, leaf_thickness/2])
            cube([0.2, length, leaf_thickness * 0.5], center = true);
    }

    // Add some curl to the leaf
    rotate([curl * 10, 0, 0])
        translate([0, 0, leaf_thickness])
            scale([width * 0.9, length * 0.9, leaf_thickness * 0.7])
                sphere(r = 1, $fn = 16);
}

module tea_leaves() {
    // Generate multiple tea leaves with random positions and orientations
    for(i = [0:20]) {
        angle = i * 137.5; // Golden angle for natural distribution
        radius = pow(i, 0.5) * 2;

        translate([
            cos(angle) * radius * 0.3, 
            sin(angle) * radius * 0.3, 
            1 + i * 0.1
        ]) {
            rotate([0, 0, angle + 90])
                rotate([rand(i) * 20 - 10, rand(i + 100) * 10 - 5, 0])
                    tea_leaf(
                        5 + rand(i * 2) * 3, 
                        1.5 + rand(i * 3) * 0.5, 
                        rand(i * 5) - 0.5
                    );
        }
    }
}

// Helper function for pseudo-random numbers
function rand(seed) = (seed * 9301 + 49297) % 233280 / 233280;

// Main assembly
color([0.9, 0.9, 0.85]) bowl();
color([0.2, 0.5, 0.1]) tea_leaves();
